import React from "react";
import {Form, Input, Button, Alert} from 'reactstrap';
import HeaderPage from "./header";
import FooterPage from "./footer";
// import HomePage from "./home";
import SidebarPage from "./sidebar";
import firebase from '../config';
import ReactQuill from "react-quill";
import 'react-quill/dist/quill.snow.css';
import SimpleReactValidator from "simple-react-validator";

class AddCoupon extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            couponCode: "",
            couponDescription: "",
            type: "",
            validFrom: "",
            validTo: "",
            numberOfCoupons: "",
            usageType: "",
            numberOfUsages: "",
            offerValue: "",
            offerType: "",
            created_on: "",
            datebasedDiv: false,
            countbasedDiv: false,
            minValue:'',
            date: new Date().toLocaleString()
        }

        this.onChange = this
            .onChange
            .bind(this);
        this.handleSubmit = this
            .handleSubmit
            .bind(this);

        this.validator = new SimpleReactValidator({
            className: "text-danger",
            validators: {

                whitespace: {
                    message: "The :attribute not allowed first whitespace   characters.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /[^\s\\]/) && params.indexOf(val) === -1);
                    }
                },
                specialChar: {
                    message: "The :attribute not allowed special   characters.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /^[ A-Za-z0-9_@./#&+-]*$/i) && params.indexOf(val) === -1);
                    }
                },
                specialCharText: {
                    message: "The :attribute may only contain letters, dot and spaces.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /^[ A-Za-z_@./#&+-]*$/i) && params.indexOf(val) === -1);
                    }
                }
            }
        });

    }

    onChange = (event) => {

        if (event.target.name == 'type') {

            if (event.target.value == "datebased") {
                this.setState({datebasedDiv: true, countbasedDiv: false});
            } else if (event.target.value == "countbased") {
                this.setState({countbasedDiv: true, datebasedDiv: false});

            }
        }

        if (event.target.name == 'usageType') {

            if (event.target.value == "Multiple") {
                this.setState({numberOfUsagesDiv: true});
            } else {
                this.setState({numberOfUsagesDiv: false});
            }
        }

        this.setState({
            [event.target.name]: event.target.value
        });

        console.log(this.state);
    };
    promoCodeChange = (event) => {
        var A = event.target.value;

        var B = A.toUpperCase();

        this.setState({
            [event.target.name]: B
        });
        
    };
    handleSubmit = (event) => {
        event.preventDefault();
        if (this.validator.allValid()) {
            if (this.state.couponCode && this.state.couponCode.trim().length !== 0) {
                event.preventDefault();
                var sessionId = sessionStorage.getItem("RoleId");

                let dbCon = firebase
                    .database()
                    .ref('/Coupons');
                dbCon.push({
                    couponCode: this.state.couponCode,
                    couponDescription: this.state.couponDescription,
                    type: this.state.type,
                    validFrom: this.state.validFrom,
                    validTo: this.state.validTo,
                    numberOfCoupons: this.state.numberOfCoupons,
                    usageType: this.state.usageType,
                    numberOfUsages: this.state.numberOfUsages,
                    offerValue: this.state.offerValue,
                    offerType: this.state.offerType,
                    minValue:this.state.minValue,
                    created_on: this.state.date,
                    sessionId: sessionId
                });

                this.setState({
                    couponCode: "",
                    couponDescription: "",
                    type: "",
                    validFrom: "",
                    validTo: "",
                    numberOfCoupons: "",
                    usageType: "",
                    numberOfUsages: "",
                    offerValue: "",
                    offerType: "",
                    created_on: "",
                    minValue:'',
                    datebasedDiv: false,
                    countbasedDiv: false
                });
                this
                    .props
                    .history
                    .push("/Coupons");
            }

        } else {
            this
                .validator
                .showMessages();
            this.forceUpdate();
        }
    };

    render() {

        const divStyle = {
            'height': '300px'
        };

        return (
            <div>
                <SidebarPage/>

                <div className="all-content-wrapper">
                    <HeaderPage/>

                    <div className="breadcome-area">
                        <div className="container-fluid">
                            <div className="row">
                                <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                    <div className="breadcome-list">
                                        <div className="row">
                                            <div className="col-lg-6 col-md-6 col-sm-6 col-xs-6">
                                                <div className="breadcomb-wp">

                                                    <div className="breadcomb-ctn">
                                                        <h2>Coupons</h2>
                                                    </div>
                                                </div>
                                            </div>

                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                    <div className="single-product-tab-area mg-b-30">

                        <div className="single-pro-review-area">
                            <div className="container-fluid">
                                <div className="row">
                                    <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                        <div className="review-tab-pro-inner">

                                            <h4>Add Coupon</h4>

                                            <div className="product-tab-list tab-pane fade active in">
                                                <form
                                                    id="couponsform"
                                                    className="form-horizontal"
                                                    role="form"
                                                    onSubmit={this.handleSubmit}>
                                                    <div className="form-group">
                                                        <label className="col-sm-2 control-label">Coupon Code<span style={{
                color: 'red'
            }}>*</span>
                                                        </label>
                                                        <div className="col-sm-8">
                                                            <input
                                                                className="form-control required"
                                                                type="text"
                                                                onChange={this.promoCodeChange}
                                                                value={this.state.couponCode}
                                                                name="couponCode"
                                                                id="couponCode"
                                                                placeholder="Coupon Code*"
                                                                maxLength={6}/> {this
                                                                .validator
                                                                .message("Coupon Code", this.state.couponCode, "required|whitespace|specialChar|min:3|max:70")}
                                                            <div className="text-danger">
                                                                {" "}
                                                                {this.state.employer_sevice_message}
                                                            </div>
                                                            <span
                                                                style={{
                                                                color: 'red'
                                                            }}/>
                                                        </div>
                                                    </div>
                                                    <div className="form-group">
                                                        <label className="col-sm-2 control-label">Coupon Description<span style={{
                color: 'red'
            }}>*</span>
                                                        </label>
                                                        {this
                                                            .validator
                                                            .message("Coupon Code", this.state.couponCode, "required|whitespace|specialChar|min:3|max:70")}

                                                        <div className="col-sm-8">
                                                            <textarea
                                                                className="form-control required"
                                                                rows={5}
                                                                cols={30}
                                                                name="couponDescription"
                                                                onChange={this.onChange}
                                                                value={this.state.couponDescription}
                                                                id="couponDescription"
                                                                placeholder="Coupon Description*"
                                                                spellCheck="false"/> {this
                                                                .validator
                                                                .message("Coupon Description", this.state.couponDescription, "required|whitespace|min:3|max:150")}

                                                            <span
                                                                style={{
                                                                color: 'red'
                                                            }}/>
                                                        </div>
                                                    </div>
                                                    <div className="form-group">
                                                        <label className="col-sm-2 control-label">Coupon Type<span style={{
                color: 'red'
            }}>*</span>
                                                        </label>
                                                        <div className="col-sm-8">
                                                            <label className="radio-inline no-padd">
                                                                <input
                                                                    type="radio"
                                                                    name="type"
                                                                    onChange={this.onChange}
                                                                    value="datebased"
                                                                    className="icheck"
                                                                    checked={this.state.type === 'datebased'}/>
                                                                Duration &nbsp;&nbsp;</label>
                                                            <label className="radio-inline no-padd">
                                                                <input
                                                                    type="radio"
                                                                    name="type"
                                                                    onChange={this.onChange}
                                                                    value="countbased"
                                                                    className="icheck" 
                                                                    checked={this.state.type === 'countbased'}/>
                                                                Count Based</label>
                                                            <span
                                                                style={{
                                                                color: 'red'
                                                            }}/>
                                                        </div>
                                                        <div className ="col-sm-4"></div>
                                                        {this.validator.message("Coupon Type", this.state.type, "required")}
                                                       
                                                    </div>
                                                   
                                                    <div
                                                        style={{
                                                        display: this.state.datebasedDiv
                                                            ? ''
                                                            : 'none'
                                                    }}
                                                        id="datebaseddiv">
                                                        <div className="form-group setting-row">
                                                            <label className="col-sm-3 control-label">From</label>
                                                            <div className="col-sm-6">
                                                                <input
                                                                    name="validFrom"
                                                                    id="validFrom"
                                                                    size={16}
                                                                    type="date"
                                                                    onChange={this.onChange}
                                                                    value={this.state.validFrom}
                                                                    className="form-control"/>
                                                                <span
                                                                    style={{
                                                                    color: 'red'
                                                                }}/>
                                                            </div>
                                                        </div>
                                                        <div className="form-group setting-row">
                                                            <label className="col-sm-3 control-label">To</label>
                                                            <div className="col-sm-6">
                                                                <input
                                                                    name="validTo"
                                                                    id="validTo"
                                                                    className="form-control"
                                                                    size={16}
                                                                    type="date"
                                                                    onChange={this.onChange}
                                                                    value={this.state.validTo}/>
                                                                <span
                                                                    style={{
                                                                    color: 'red'
                                                                }}/>
                                                            </div>
                                                        </div>
                                                    </div>
                                                    <div
                                                        className="form-group setting-row"
                                                        id="countbased"
                                                        style={{
                                                        display: this.state.countbasedDiv
                                                            ? ''
                                                            : 'none'
                                                    }}>
                                                        <label htmlFor="name" className="col-sm-3 control-label">Count</label>
                                                        <div className="col-sm-8"><input
                                                            id="numberOfCoupons"
                                                            name="numberOfCoupons"
                                                            onChange={this.onChange}
                                                            value={this.state.numberOfCoupons}
                                                            type="number"
                                                            placeholder="Count"
                                                            className="form-control"/>
                                                            <span
                                                                style={{
                                                                color: 'red'
                                                            }}/>
                                                        </div>
                                                       
                                                    </div>
                                                    <div className="form-group">
                                                        <label className="col-sm-2 control-label">Usage<span style={{
                color: 'red'
            }}>*</span>
                                                        </label>
                                                        <div className="col-sm-8">
                                                            <label className="radio-inline no-padd">
                                                                <input
                                                                    type="radio"
                                                                    name="usageType"
                                                                    value="Once"
                                                                    onChange={this.onChange}
                                                                    className="icheck"/>
                                                                Once &nbsp;&nbsp;</label>
                                                            <label className="radio-inline no-padd">
                                                                <input
                                                                    type="radio"
                                                                    name="usageType"
                                                                    value="Multiple"
                                                                    onChange={this.onChange}
                                                                    className="icheck"/>
                                                                Multiple</label>
                                                            <input
                                                                type="number"
                                                                name="numberOfUsages"
                                                                onChange={this.onChange}
                                                                value={this.state.numberOfUsages}
                                                                style={{
                                                                display: this.state.numberOfUsagesDiv
                                                                    ? ''
                                                                    : 'none'
                                                            }}
                                                                id="numberOfUsages"
                                                                className="valid"/>
                                                            <span
                                                                style={{
                                                                color: 'red'
                                                            }}/>
                                                        </div>
                                                    </div>
                                                    <div className="form-group">
                                                        <label className="col-sm-2 control-label">Minimum Value<span style={{
                color: 'red'
            }}>*</span>
                                                        </label>
                                                        <div className="col-sm-8">
                                                            <input
                                                                className="form-control required"
                                                                type="text"
                                                                onChange={this.onChange}
                                                                value={this.state.minValue}
                                                                name="minValue"
                                                             
                                                                placeholder="Minimum Value*"
                                                                maxLength={6}/> {this
                                                                .validator
                                                                .message("Minimum Value", this.state.minValue, "required|whitespace|specialChar|min:3|max:70")}
                                                           
                                                            <span
                                                                style={{
                                                                color: 'red'
                                                            }}/>
                                                        </div>
                                                    </div>
                                                    <div className="form-group setting-row">
                                                        <label htmlFor="name" className="col-sm-3 control-label">Offer</label>
                                                        <div className="col-sm-3">
                                                            <input
                                                                type="text"
                                                                placeholder="Offer"
                                                                name="offerValue"
                                                                onChange={this.onChange}
                                                                value={this.state.offerValue}
                                                                id="offerValue"
                                                                className="form-control required"/>
                                                        </div>
                                                        <div className="col-sm-6">
                                                            <div>
                                                                <label className="radio-inline no-padd">
                                                                    <input
                                                                        type="radio"
                                                                        name="offerType"
                                                                        onChange={this.onChange}
                                                                        value="%"
                                                                        defaultChecked
                                                                        className="icheck"/>
                                                                    % &nbsp;&nbsp;&nbsp;</label>
                                                                <label className="radio-inline no-padd">
                                                                    <input
                                                                        type="radio"
                                                                        name="offerType"
                                                                        onChange={this.onChange}
                                                                        value="Inr"
                                                                        className="icheck"/>
                                                                    INR</label>
                                                            </div>
                                                        </div>
                                                    </div>
                                                    <div className="form-group">
                                                        <div className="col-sm-offset-2 col-sm-10">
                                                            <button type="submit" className="btn btn-success">Save</button>
                                                        </div>

                                                    </div>

                                                </form>

                                            </div>

                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
                <FooterPage/>

            </div>
        );
    }
}

export default AddCoupon;
