import React from "react";
import {Link} from "react-router-dom";
import {Form} from 'reactstrap';
import HeaderPage from "./header";
import FooterPage from "./footer";
import SidebarPage from "./sidebar";
import firebase from '../config';
import SimpleReactValidator from "simple-react-validator";
import FileUploader from "react-firebase-file-uploader";

class EditEmployee extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            location: '',
            error_message: '',
            date: new Date().toLocaleString(),
            validError: false,
            uploadImage: '',
            avatar: "",
            isUploading: false,
            progress: 0,
            avatarURL: ""
        }

        this.validator = new SimpleReactValidator({
            className: "text-danger",
            validators: {

                whitespace: {
                    message: "The :attribute not allowed first whitespace   characters.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /[^\s\\]/) && params.indexOf(val) === -1);
                    }
                },
                specialChar: {
                    message: "The :attribute not allowed special   characters.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /^[ A-Za-z0-9_@./#&+-]*$/i) && params.indexOf(val) === -1);
                    }
                },
                specialCharText: {
                    message: "The :attribute may only contain letters, dot and spaces.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /^[ A-Za-z_@./#&+-]*$/i) && params.indexOf(val) === -1);
                    }
                }
            }
        });

    }
    componentDidMount() {
        const {employeeID} = this.props.match.params;
        console.log(employeeID);
        var sessionId = sessionStorage.getItem("RoleId"); 
        var ref = firebase
            .database()
            .ref(`Employees/${employeeID}`);

        ref.on('value', snapshot => {
            var data = snapshot.val();

            // console.log(categories)

            this.setState({
                first_name: data.first_name,
                last_name: data.last_name,
                email: data.email,
                designation:data.designation,
                mobile_number: data.mobile_number,
                department:data.department,
                city:data.city,
                avatarURL:data.uploadImage,
                address:data.address,
                phone_emergency:data.phone_emergency,
                reporting_manager:data.reporting_manager,
                date:data.date,
                sessionId:sessionId,
                });
            console.log(this.state.location);
        });
        this.city();
        this.department();
        this.designation();


    }

    city=()=> {
       
        var ref = firebase.database().ref("City/");
        ref.on('value', snapshot => {
            const data = [];
            snapshot.forEach(childSnapShot => {
               
              const cities = {
                cityId: childSnapShot.key.toString(),
                city: childSnapShot.val().city,
              };
              data.push(cities);
            });
            this.setState({ City:data,loading: false });
            console.log(this.state.City);
          }); 
         
    }
    department=()=>{
      var ref = firebase.database().ref("departments/");
      ref.on('value', snapshot => {
          const data = [];
          snapshot.forEach(childSnapShot => {
             
            const departments = {
              departmentId: childSnapShot.key.toString(),
              department: childSnapShot.val().department,
            };
            data.push(departments);
          });
          this.setState({ departments:data,loading: false });
          console.log(this.state.departments);
        }); 
    }
    designation=()=>{
      var ref = firebase.database().ref("designation/");
      ref.on('value', snapshot => {
          const data = [];
          snapshot.forEach(childSnapShot => {
             
            const designations = {
              designationId: childSnapShot.key.toString(),
              designation: childSnapShot.val().designation,
            };
            data.push(designations);
          });
          this.setState({ designations:data,loading: false });
          console.log(this.state.designations);
        }); 
    }
    

    handleUploadStart = () => this.setState({ isUploading: true, progress: 0 });
  handleProgress = progress => this.setState({ progress });
  handleUploadError = error => {
    this.setState({ isUploading: false });
    console.error(error);
  };
  handleUploadSuccess = filename => {
    this.setState({ avatar: filename, progress: 100, isUploading: false });
    firebase
      .storage()
      .ref("uploadFiles")
      .child(filename)
      .getDownloadURL()
      .then(url => this.setState({ avatarURL: url }));

      //console.log(this.state.avatarURL);
  };

    handleChange = (event) => {
        this.setState({
            [event.target.name]: event.target.value
        });

        // var ref = firebase
        //     .database()
        //     .ref('Locations/')
        //     .orderByChild("location")
        //     .equalTo(event.target.value);
        // ref.on('value', snapshot => {
        //     var user_exist = snapshot.numChildren();
        //     console.log(user_exist);

        //     if (user_exist > 0 && this.state.validError!=true) {

        //         this.setState({error_message: "Locations Name is already exist", validError: false});
        //     } else {
        //         this.setState({error_message: "", validError: true});

        //     }

        // })

    };
    handleSubmit = (event) => {
        event.preventDefault();
        if (this.validator.allValid() && this.state.validError == true) {
            const {employeeID} = this.props.match.params;
            var sessionId = sessionStorage.getItem("RoleId"); 
            let dbCon = firebase
                .database()
                .ref(`/Employees/${employeeID}`);
            console.log(dbCon);
            dbCon.update({
                employeeId: Math.round((new Date().getTime() / 1000)),
                role:"employee",
                first_name: this.state.first_name,
                last_name: this.state.last_name,
                email: this.state.email,
                designation: this.state.designation,
                mobile_number: this.state.mobile_number,
                department: this.state.department,
                city: this.state.city,
                uploadImage: this.state.avatarURL,
                address: this.state.address,
                phone_emergency:this.state.phone_emergency,
                reporting_manager:this.state.reporting_manager,
                password:this.state.password,
                status:'Active',
                sessionId:sessionId,
                date: this.state.date,
            });

            this
                .props
                .history
                .push("/EmployeeList/Updated");

        } else {
            this
                .validator
                .showMessages();
            this.forceUpdate();
        }
    };

    render() {

        return (
            <div>
                <SidebarPage/>

                <div className="all-content-wrapper">
                    <HeaderPage/>

                    <div className="breadcome-area">
                        <div className="container-fluid">
                            <div className="row">
                                <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                    <div className="breadcome-list">
                                        <div className="row">
                                            <div className="col-lg-6 col-md-6 col-sm-6 col-xs-6">
                                                <div className="breadcomb-wp">

                                                    <div className="breadcomb-ctn">
                                                        <h2>Locations</h2>
                                                    </div>
                                                </div>
                                            </div>

                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                    <div className="single-product-tab-area mg-b-30">

                        <div className="single-pro-review-area">
                            <div className="container-fluid">
                                <div className="row">
                                    <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                        <div className="review-tab-pro-inner">

                                            <h4>Edit Location</h4>

                                            <div className="product-tab-list tab-pane fade active in">
                                            <Form onSubmit={this.handleSubmit} id="Add-products">

<div className="row m-b-20">
    <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
        <label>First Name<span style={{color: 'red'}}>*</span>
        </label>
        <input
            type="text"
            className="form-control"
            placeholder=""
            name="first_name"
            onChange={this.handleChange}
            value={this.state.first_name}/> {this
            .validator
            .message("First Name", this.state.first_name, "required|specialCharText|whitespace|min:2|max:70")}
        <div className="text-danger">
            {" "}
            {this.state.error_message}
        </div>
    </div>
    <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
        <label>Last Name<span style={{color: 'red'}}>*</span>
        </label>
        <input
            type="text"
            className="form-control"
            placeholder=""
            name="last_name"
            onChange={this.handleChange}
            value={this.state.last_name}/> {this
                .validator
                .message("First Name", this.state.last_name, "required|specialCharText|whitespace|min:2|max:70")}
            <div className="text-danger">
                {" "}
                {this.state.error_message}
        </div>
    </div>
    <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
        <label>E-mail Address<span style={{color: 'red'}}>*</span>
        </label>
        <input
            type="text"
            className="form-control"
            placeholder=""
            name="email"
            id="price_cost"
            onChange={this.handleChange}
            value={this.state.email}/> {this
            .validator
            .message("E-mail Address", this.state.email, "required|email")}

    </div>
   
    <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
        <label>Department<span style={{color: 'red' }}>*</span></label>

        <select
            name="select"
            className="form-control pro-edt-select form-control-primary"
            name="department"
            onChange={this.handleChange}>
            <option>Select Department</option>
            {this.state.departments && this
                .state
                .departments
                .map((data, index) => {

                    return(
                        <option value={data.department} key={index}>{data.department}</option>
                      )
             } )}
            </select>
            
             {this.validator.message("Department", this.state.department, "required")}

      

    </div>

  

    <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
        <label>Designation<span style={{color: 'red' }}>*</span></label>

        <select
            name="select"
            className="form-control pro-edt-select form-control-primary"
            name="designation"
            onChange={this.handleChange}>
            <option>Select Designation</option>
            {this.state.designations && this
                .state
                .designations
                .map((data, index) => {

                    return(
                        <option value={data.designation} key={index}>{data.designation}</option>
                      )
             } )}
            </select>
            
             {this.validator.message("Select Designation", this.state.designation, "required")}

    </div>



        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
        <label>City<span style={{color: 'red' }}>*</span></label>

        <select
            name="select"
            className="form-control pro-edt-select form-control-primary"
            name="city"
            onChange={this.handleChange}>
            <option>Select City</option>
            {this.state.City && this
                .state
                .City
                .map((data, index) => {

                    return(
                        <option value={data.city} key={index}>{data.city}</option>
                      )
             } )}
            </select>
            
             {this.validator.message("City", this.state.city, "required")}

      

    </div>
    <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
        <label>Mobile Number<span style={{
color: 'red'
}}>*</span>
        </label>
        <input
            type="text"
            className="form-control"
            placeholder=""
            name="mobile_number"
            onChange={this.handleChange}
            value={this.state.mobile_number}/> {this
            .validator
            .message(" Mobile Number", this.state.mobile_number, "required||numeric|max:10")}

    </div>

 
     <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
        <label>Phone Number (For emergency conatact)<span style={{
color: 'red'
}}>*</span>
        </label>
        <input
            type="text"
            className="form-control"
            placeholder=""
            name="phone_emergency"
            onChange={this.handleChange}
            id="phone_emergency"
            value={this.state.phone_emergency}/> {this
            .validator
            .message(" Phone Number", this.state.phone_emergency, "required|numeric|min:1|max:10")}

    </div>

    <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
        <label>Reporting Manager<span style={{
color: 'red'
}}>*</span>
        </label>
           <select
            className="form-control"
            placeholder=""
            name="reporting_manager"
            value={this.state.reporting_manager}
            onChange={this.handleChange}>
            <option value="" label="Select" selected="selected">Select</option>
            

            <option value="1" label="1">1</option>
            <option value="2" label="2">2</option>
            <option value="3" label="3">3</option>
           
        </select>
          {this
            .validator
            .message(" Reporting Manager", this.state.reporting_manager, "required")}

    </div>
    <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
        <label>Address<span style={{
color: 'red'
}}>*</span>
        </label>
        <textarea
            type="text"
            className="form-control"
            placeholder=""
            name="address"
            onChange={this.handleChange}
           
            value={this.state.address}/> {this
            .validator
            .message("Addresss", this.state.address, "required|min:5|max:150")}

    </div>
    {/* <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
        <label>Password<span style={{ color: 'red'}}>*</span>
        </label>
        <input
         type="Password"
         name="password"
         value=""
         className="form-control"
      
        
           />
    </div> */}
   
</div>

 <div className="row m-b-20">
 <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
        <div className="dropzone-pro">
            <div id="dropzone">

                <div className="dz-message needsclick download-custom">
                    <i className="fa fa-cloud-download" aria-hidden="true"></i>
                    <h2>Upload Photo<span style={{color: 'red' }}>*</span></h2>

                </div>

                {this.state.isUploading && <p>Progress: {this.state.progress}</p>}
                {this.state.avatarURL && <img
                    src={this.state.avatarURL}
                    style={{
                    height: "200px",
                    width: "250px"
                }}/>}
                <FileUploader
                    accept="image/*"
                    name="avatar"
                    randomizeFilename
                    storageRef={firebase
                    .storage()
                    .ref("images")}
                    onUploadStart={this.handleUploadStart}
                    onUploadError={this.handleUploadError}
                    onUploadSuccess={this.handleUploadSuccess}
                    onProgress={this.handleProgress}/> {this
                    .validator
                    .message("Upload  Image", this.state.avatarURL, "required")}

            </div>
        </div>

    </div>

  

</div> 
  {/* <div className="row m-b-20">
                                                     <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <div className="dropzone-pro">
                                                                <div id="dropzone">

                                                                    <div className="dz-message needsclick download-custom">
                                                                        <i className="fa fa-cloud-download" aria-hidden="true"></i>
                                                                        <h2>Upload Photo<span style={{color: 'red' }}>*</span></h2>

                                                                    </div>

                                                                    {this.state.isUploading && <p>Progress: {this.state.progress}</p>}
                                                                    {this.state.avatarURL && <img
                                                                        src={this.state.avatarURL}
                                                                        style={{
                                                                        height: "200px",
                                                                        width: "250px"
                                                                    }}/>}
                                                                    <FileUploader
                                                                        accept="image/*"
                                                                        name="avatar"
                                                                        randomizeFilename
                                                                        storageRef={firebase
                                                                        .storage()
                                                                        .ref("images")}
                                                                        onUploadStart={this.handleUploadStart}
                                                                        onUploadError={this.handleUploadError}
                                                                        onUploadSuccess={this.handleUploadSuccess}
                                                                        onProgress={this.handleProgress}/> {this
                                                                        .validator
                                                                        .message("Upload  Image", this.state.avatarURL, "required")}



                                                                </div>
                                                            </div>

                                                        </div>

                                                      

                                                    </div>  */}




<div className="row">
    <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
        <div className="text-center custom-pro-edt-ds">
            <button
                type="submit"
                className="btn btn-ctl-bt waves-effect waves-light m-r-10">Save
            </button>
            <Link to="/Locations">
                <button type="button" className="btn btn-ctl-bt waves-effect waves-light">Cancel
                </button>
            </Link>
        </div>
    </div>
</div>
</Form>
                                            </div>

                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
                <FooterPage/>

            </div>
        );
    }
}
export default EditLocation;
