import React from "react";
import {Link} from "react-router-dom";
import {Form} from 'reactstrap';
import HeaderPage from "./header";
import FooterPage from "./footer";
import SidebarPage from "./sidebar";
import firebase from '../config';
import SimpleReactValidator from "simple-react-validator";
class EditRolePage extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            Role: '',
            dashboard: '',
            orders: '',
            products: '',
            category: '',
            subCategory: '',
            invoices: '',
            abandonedCart: '',
            coupons: '',
            merchantPayments: '',
            AdminStaff: '',
            merchantMangement: '',
            notifications: '',
            users: '',
            settings: '',
            Analytics: '',
            sliders: '',
            enquiry: '',
            date: new Date().toLocaleString()
        }
        this.validator = new SimpleReactValidator({
            className: "text-danger",
            validators: {

                whitespace: {
                    message: "The :attribute not allowed first whitespace   characters.",
                    rule: function (val, params, validator) {
                        return (validator.helpers.testRegex(val, /[^\s\\]/) && params.indexOf(val) === -1);
                    }
                },
                specialChar: {
                    message: "The :attribute not allowed special   characters.",
                    rule: function (val, params, validator) {

                        return (validator.helpers.testRegex(val, /^[ A-Za-z0-9_@./#&+-]*$/i) && params.indexOf(val) === -1);
                    }
                },
                specialCharText: {
                    message: "The :attribute may only contain letters, dot and spaces.",
                    rule: function (val, params, validator) {
                        return (validator.helpers.testRegex(val, /^[ A-Za-z_@./#&+-]*$/i) && params.indexOf(val) === -1);
                    }
                }
            }
        });

    }
    componentDidMount() {
        const {GstID} = this.props.match.params;
        var ref = firebase
            .database()
            .ref(`Role/${GstID}`);
        ref.on('value', snapshot => {
            var gstData = snapshot.val();
            this.setState({
                Role: gstData.Role,
                dashboard: gstData.dashboard,
                orders: gstData.orders,
                products: gstData.products,
                category: gstData.category,
                subCategory: gstData.subCategory,
                invoices: gstData.invoices,
                abandonedCart: gstData.abandonedCart,
                coupons: gstData.coupons,
                merchantPayments: gstData.merchantPayments,
                AdminStaff: gstData.AdminStaff,
                merchantMangement: gstData.merchantMangement,
                notifications: gstData.notifications,
                users: gstData.users,
                settings: gstData.settings,
                Analytics: gstData.Analytics,
                sliders: gstData.sliders,
                enquiry: gstData.enquiry,
                loading: false
            });
            console.log(this.state.Role);
        });

    }
    onChange = (event) => {

        this.setState({
            [event.target.name]: event.target.value
        });
    };

    handleSubmit = (event) => {
        if (this.validator.allValid()) {
            const {GstID} = this.props.match.params;

            if (this.state.Role && this.state.Role.trim().length !== 0) {
                event.preventDefault();
                let dbCon = firebase
                    .database()
                    .ref(`/Role/${GstID}`);
                dbCon.update({
                    Role: this.state.Role,
                    dashboard: this.state.dashboard,
                    orders: this.state.orders,
                    products: this.state.products,
                    category: this.state.category,
                    subCategory: this.state.subCategory,
                    invoices: this.state.invoices,
                    abandonedCart: this.state.abandonedCart,
                    coupons: this.state.coupons,
                    merchantPayments: this.state.merchantPayments,
                    AdminStaff: this.state.AdminStaff,
                    merchantMangement: this.state.merchantMangement,
                    notifications: this.state.notifications,
                    users: this.state.users,
                    settings: this.state.settings,
                    Analytics: this.state.Analytics,
                    sliders: this.state.sliders,
                    enquiry: this.state.enquiry,
                    created_on: this.state.date
                });
                this.addNotification();

                this.setState({Role: ''});
                this
                    .props
                    .history
                    .push("/RoleListPage/Updated");
            }
        } else {
            this
                .validator
                .showMessages();
            this.forceUpdate();
        }
    };

    render() {

        return (
            <div>
                <SidebarPage/>

                <div className="all-content-wrapper">
                    <HeaderPage/>

                    <div className="breadcome-area">
                        <div className="container-fluid">
                            <div className="row">
                                <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                    <div className="breadcome-list">
                                        <div className="row">
                                            <div className="col-lg-6 col-md-6 col-sm-6 col-xs-6">
                                                <div className="breadcomb-wp">

                                                    <div className="breadcomb-ctn">
                                                        <h2>Role</h2>
                                                    </div>
                                                </div>
                                            </div>

                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                    <div className="single-product-tab-area mg-b-30">

                        <div className="single-pro-review-area">
                            <div className="container-fluid">
                                <div className="row">
                                    <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                        <div className="review-tab-pro-inner">

                                            <h4>Edit Role</h4>

                                            <div className="product-tab-list tab-pane fade active in">
                                                <Form onSubmit={this.handleSubmit} id="Edit-Role">

                                                    <div className="row m-b-20">
                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <label >Role<span style={{
                color: 'red'
            }}>*</span>
                                                            </label>
                                                            <input
                                                                type="text"
