import React from "react";
import {Link} from "react-router-dom";
import HeaderPage from "./header";
import FooterPage from "./footer";
import SidebarPage from "./sidebar";
import firebase from '../config';
import swal from 'sweetalert';
import $ from 'jquery';
$.DataTable = require('datatables.net');

class Files extends React.Component {
    constructor(props) {
        super(props);
        this.datatable = null;
        this.state = {
            file: '',
            name: '',
            description: '',
            permission:'',
            specifEmpName:'',
            FileList: [],
            isUploading: false,
            error_message: '',
            created_on: new Date().toLocaleString(),
            validError: true,
            loading: false,
            count: 1,
            activePage: "1",
            countPage: "",
            usePermList: ""
        };

    }
   
    componentDidMount(){
        var ref=firebase.database().ref('Files/');
      ref.once("value",snapshot =>{
       const data =[];
        console.log(snapshot.val());
        snapshot.forEach(element => {
          const usersData={
            fileID: element.key.toString(),
            
            file:element.val().file,
            name:element.val().name,
          
            created_on:element.val().created_on,
           
    
           
          }
          data.unshift(usersData);
        });
       
        this.setState({Files:data},()=>{
          console.log(this.state.Files,'Files');
        });
      })
      }
    componentWillUnmount() {
        
        clearTimeout(this.interval);
    }
    deleteItem = id => {

        swal({title: "Are you sure?", text: "Do your really want to remove?", icon: "warning", buttons: true, dangerMode: true}).then(willDelete => {
            if (willDelete) {
                console.log(id);
                var playersRef = firebase
                    .database()
                    .ref(`/Files/${id}`);
                playersRef.remove();

                this
                    .props
                    .history
                    .push("/Files/Deleted");
            } else {}
          

        });

    };

    render() {

        // var pathname = this
        //     .props
        //     .location
        //     .pathname
        //     .split("/");
        // var alertName = pathname[2];
        return (
            <div>
                <SidebarPage/>

                <div className="all-content-wrapper">
                    <HeaderPage/>

                    <div className="breadcome-area">
                        <div className="container-fluid">
                            <div className="row">
                                <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                    <div className="breadcome-list">
                                        <div className="row">
                                            <div className="col-lg-6 col-md-6 col-sm-6 col-xs-6">
                                                <div className="breadcomb-wp">

                                                    <div className="breadcomb-ctn">
                                                        <h2>Files</h2>

                                                    </div>

                                                </div>
                                            </div>

                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                    <div className="product-status mg-b-30">
                        <div className="container-fluid">
                            <div className="row">
                                <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                    <div className="product-status-wrap">
                                        <h4>Files List</h4>

                                        <div className="add-product">
                                                    <Link to='/AddFiles'>Add File</Link>
                                                </div>
{/*                                           
                                        {alertName
                                            ? <div
                                                    className="alert alert-success display-none"
                                                    style={{
                                                    display: "block"
                                                }}>
                                                    <a data-dismiss="alert" href="#" aria-hidden="true" className="close">×</a>
                                                    Files {alertName}
                                   
                                                    Successfully
                                                </div>
                                            : ''} */}
                                        <table className="table table-bordered paginationTable">
                                            <thead>
                                                <tr>
                                                    <th>S.No</th>
                                                    <th>File Name</th>
                                                    <th>uploaded On</th>
                                                    <th>uploaded By</th>
                                                  
                                                  

                                                </tr>
                                            </thead>
                                            <tbody>

                                                {this
                                                    .state
                                                    .Files
                                                    .map((data, index) => {
                                                        console.log(data);

                                                        return (
                                                            <tr key={index}>
                                                                <td>{index + 1}</td>
                                                                <td>{data.file}</td>
                                                                <td>{data.name} </td>
                                                                <td>{data.created_on}
                                                                </td>
                                                                <td>

                                                                            <Link to={`/EditFile/${data.fileID}`}>
                                                                                <button data-toggle="tooltip" title="Edit" className="pd-setting-ed">
                                                                                    <i className="fa fa-pencil-square-o" aria-hidden="true"></i>
                                                                                </button>
                                                                            </Link>

                                                                            <button
                                                                                data-toggle="tooltip"
                                                                                title="Trash"
                                                                                className="pd-setting-ed"
                                                                                data-id={data.fileID}
                                                                                onClick={this
                                                                                .deleteItem
                                                                                .bind(this, data.fileID)}>
                                                                                <i className="fa fa-trash-o" aria-hidden="true"></i>
                                                                            </button>
                                                                        </td>
                                                                 

                                                            </tr>
                                                        )

                                                    })}

                                            </tbody>
                                        </table>

                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                </div>

                <FooterPage/>

            </div>
        );
    }
}

export default Files;
