import React from "react";
import { Link } from "react-router-dom";
import FadeIn from "react-fade-in";
import axios from "axios";
import HeaderPage from "./header";
import FooterPage from "./footer";
// import HomePage from "./home";
import SidebarPage from "./sidebar";
import firebase from '../config';
import swal from 'sweetalert';
import readXlsxFile from 'read-excel-file'
import {OutTable, ExcelRenderer} from 'react-excel-renderer';

import $ from 'jquery';
$.DataTable = require( 'datatables.net' );


class ServiceProviderListPage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
        serviceProviderList: [],
        loading: false,
        count: 1,
        activePage: "1",
        countPage: "",
        cols:'',
        rows:''
      };
      this.deleteItem = this.deleteItem.bind(this);

  }

  componentDidMount() {
    this.setState({ loading: true });
    var ref = firebase.database().ref("serviceProvider/");


    ref.on('value', snapshot => {
        const data = [];
        // console.log(snapshot);
        // console.log(snapshot.val);

        snapshot.forEach(childSnapShot => {
           
          const ServiceData = {
            serviceId: childSnapShot.key.toString(),
            serviceProviderName: childSnapShot.val().serviceProviderName,
            trackingURL: childSnapShot.val().trackingURL,
            created_on: childSnapShot.val().created_on,
          };
  
          data.push(ServiceData);
        });
  
        this.setState({ serviceProviderList:data, countPage: data.length,loading: false });
        console.log(this.state.serviceProviderList);
        this.interval = setTimeout(() => $('.paginationTable').DataTable(), 1000);
    });
}
componentWillUnmount() {
    clearTimeout(this.interval);
  }
  deleteItem = id => {
    
    swal({title: "Are you sure?", text: "Do your really want to remove?", icon: "warning", buttons: true, dangerMode: true}).then(willDelete => {
      if (willDelete) {
        console.log(id);
        var playersRef = firebase.database().ref(`/serviceProvider/${id}`);
        playersRef.remove();
        
  
      } else {}
     
     
  });
  
    };
  onChange =(event) =>{
    readXlsxFile(event.type.value).then((rows) => {

      console.log(rows);
      // `rows` is an array of rows
      // each row being an array of cells.
    })
  }
  fileHandler = (event) => {
    let fileObj = event.target.files[0];

    //just pass the fileObj as parameter
    ExcelRenderer(fileObj, (err, resp) => {
      if(err){
        console.log(err);            
      }
      else{
        this.setState({
          cols: resp.cols,
          rows: resp.rows
        });
      }
    });               

  }
  render() {
   


    return (
      <div>
     <SidebarPage/>
   
    <div className="all-content-wrapper">
    <HeaderPage/>
   
          
            <div className="breadcome-area">
                <div className="container-fluid">
                    <div className="row">
                        <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                            <div className="breadcome-list">
                                <div className="row">
                                    <div className="col-lg-6 col-md-6 col-sm-6 col-xs-6">
                                        <div className="breadcomb-wp">
                                        
                                            <div className="breadcomb-ctn">
                                            <h2>service Providers</h2>
                                                </div>
                                        </div>
                                    </div>
                          
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
      
     
            <div className="product-status mg-b-30">
            <div className="container-fluid">
                <div className="row">
                    <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                        <div className="product-status-wrap">
                            <h4>Service Provider List</h4>
                            <div className="add-product">
                                <Link to='/AddServicePage'>Add Service Provider</Link>
                            </div>
                            {/* <input type="file" id="input" onChange={this.onChange} /> */}
                            <input type="file" onChange={this.fileHandler.bind(this)} style={{"padding":"10px"}} />
                            <OutTable data={this.state.rows} columns={this.state.cols} tableClassName="ExcelTable2007" tableHeaderRowClass="heading" />

                            <table className="table table-bordered paginationTable">
                            <thead>
                                <tr>
                                    <th>S.No</th>
                                    <th>Name</th>
                                    <th>Tracking URL</th>
									 <th>Created on</th>
                                <th>Action</th>
                                </tr>
                                </thead>
                                <tbody>
                                 
                                    
                                    {this.state.serviceProviderList && this.state.serviceProviderList.map((data,index) =>{
                                        console.log(data);
                                       
                                        return(
                                         <tr key={index}>
                                         <td>{index+1}</td>
                                         <td>{data.serviceProviderName}</td>
                                         <td>{data.trackingURL}</td>
                                         <td>{data.created_on}	</td>
                                         <td>									
                                            
                                         <Link to={`/EditServicePage/${data.serviceId}`}>
                                         <button data-toggle="tooltip" title="Edit" className="pd-setting-ed"><i className="fa fa-pencil-square-o" aria-hidden="true"></i></button>
                                         </Link>								
                     
                                         <button data-toggle="tooltip" title="Trash" className="pd-setting-ed" data-id={data.serviceId}  onClick={this.deleteItem.bind(this, data.serviceId)}><i className="fa fa-trash-o" aria-hidden="true"></i></button>
                                         </td>
                                         
                                         </tr>)

                                    } )}
								

                   
                    </tbody>
                    </table>
                    {/* <div className="custom-pagination">
                            <Pagination
                          hideNavigation
                          activePage={this.state.activePage}
                          itemsCountPerPage={3}
                          totalItemsCount={this.state.countPage}
                          pageRangeDisplayed={3}
                          onChange={this.handlePageChange.bind(this)}
                        />
                            </div> */}
                        </div>
                    </div>
                </div>
            </div>
        </div> 
            
        </div>
        
         <FooterPage/>
    
      </div>
    );
  }
}

export default ServiceProviderListPage;
