import React from "react";
import {Form} from 'reactstrap';
import {Link} from "react-router-dom";
import HeaderPage from "./header";
import FooterPage from "./footer";
import SidebarPage from "./sidebar";
import firebase from '../config';
import SimpleReactValidator from "simple-react-validator";
import FileUploader from "react-firebase-file-uploader";
import Select from 'react-select';
import Map from './map';
import {connect} from 'react-redux';

class AddSalesTracker extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            prospect_Name: '',
            company_Name:'',
            city:'',
            contact_Number:'',
            email:'',
            source_Of_Enquiry:'',
            contacted:'',
            email_Sent:'',
            follow_Up_Date:'',
            follow_Up_Time:'',
            remarks:'',
            error_message: '',
            created_on: new Date().toLocaleString(),
            validError: true,
            employer_sevice_message:'',
            
          
          
           
        }

        this.validator = new SimpleReactValidator({
            className: "text-danger",
            validators: {

                whitespace: {
                    message: "The :attribute not allowed first whitespace   characters.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /[^\s\\]/) && params.indexOf(val) === -1);
                    }
                },
                specialChar: {
                    message: "The :attribute not allowed special   characters.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /^[ A-Za-z0-9_@./#&+-]*$/i) && params.indexOf(val) === -1);
                    }
                },
                specialCharText: {
                    message: "The :attribute may only contain letters, dot and spaces.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /^[ A-Za-z_@./#&+-]*$/i) && params.indexOf(val) === -1);
                    }
                }
            }
        });

    }
    

    componentDidMount() {

      
       
          
    
        var ref = firebase
            .database()
            .ref('Employees/');
        ref.once("value", snapshot => {
            const data = [];
            console.log(snapshot.val());
            snapshot.forEach(element => {
                const usersData = {

                    value: element
                        .val()
                        .email,
                    label: element
                        .val()
                        .first_name + " " + element
                        .val()
                        .last_name + "(" + element
                        .val()
                        .email + ")"
                }
                data.unshift(usersData);
            });

            this.setState({
                EmployeesList: data
            }, () => {
                console.log(this.state.EmployeesList, 'Employees');

            });

        })
        this.city();
    }

  city=()=>{
    var ref = firebase.database().ref("City/");
    ref.on('value', snapshot => {
        const data = [];
        snapshot.forEach(childSnapShot => {
           
          const cities = {
            cityId: childSnapShot.key.toString(),
            city: childSnapShot.val().city,
          };
          data.push(cities);
        });
        this.setState({ City:data,loading: false });
        console.log(this.state.City);
      }); 
     
  }
    handleChange = (event) => {
        console.log(this.state.validError);
        this.setState({
            [event.target.name]: event.target.value
        });

       

    };

   
    handleSubmit = (event) => {
        event.preventDefault();
        if (this.validator.allValid() && this.state.validError == true) {
         

                var sessionId = sessionStorage.getItem("RoleId");
                var userName = sessionStorage.getItem("email");

                // let dbCon = firebase.firestore().collection('/category');

                let dbCon = firebase
                    .database()
                    .ref('/Locations');
                dbCon
                    .push({
                       

            location_Name:this.state.location_Name,
            addressl:this.state.addressl,
            address2:this.state.address2,
            land_Mark:this.state.land_Mark,
            city: this.state.city,
            pincode: this.state.pincode,
            contact_Person_Name:this.state.contact_Person_Name,
            contact_Number: this.state.contact_Number,
            email:this.state.email,
            rental_Deposit_Amount:this.state.rental_Deposit_Amount,
            rent_Per_Month:this.state.rent_Per_Month,
            electricity_Per_Month:this.state.electricity_Per_Month,
            wifi_Per_Month:this.state.wifi_Per_Month,
            additional_Expenses:this.state.additional_Expenses,
            notes:this.state.notes,
            uploadImage: this.state.downloadURLs,
            created_on: this.state.created_on, 
            sessionId: sessionId,
            userName:userName,
            permission_type: this.state.permission_type,
            specifEmpName: this.state.selectedOption,
            locality: this.props.posts.locality,
            landMark: this.props.posts.land_mark,
            fullAddress: this.props.posts.full_address,
            latitude: this.props.posts.latitude,
            longitude: this.props.posts.longitude,
                    })
                    .then(response => {
                        try {
                            console.log(response);

                            this
                                .props
                                .history
                                .push("/Locations/Created");

                        } catch (error) {
                            console.log(error.config);
                        }
                    });

            
        } else {
            this
                .validator
                .showMessages();
            this.forceUpdate();
        }

    };

    render() {
        const {EmployeesList, selectedOption} = this.state;

        const divStyle = {
            'height': '40px',
            'resize':'none',
        };
        return (
            <div>
                <SidebarPage/>

                <div className="all-content-wrapper">
                    <HeaderPage/>

                    <div className="breadcome-area">
                        <div className="container-fluid">
                            <div className="row">
                                <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                    <div className="breadcome-list">
                                        <div className="row">
                                            <div className="col-lg-6 col-md-6 col-sm-6 col-xs-6">
                                                <div className="breadcomb-wp">

                                                    <div className="breadcomb-ctn">
                                                        <h2>Location</h2>

                                                    </div>

                                                </div>
                                            </div>

                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                    <div className="single-product-tab-area mg-b-30">

                        <div className="single-pro-review-area">
                            <div className="container-fluid">
                                <div className="row">
                                    <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                        <div className="review-tab-pro-inner">

                                            <h4>Add Location</h4>

                                            <div className="product-tab-list tab-pane fade active in">

                                            <Form onSubmit={this.handleSubmit} id="Add-products">

<div className="row m-b-20 class-row">
    <div className="col-lg-2 col-md-2 col-sm-2 col-xs-12">
        <label>Prospect Name <span style={{color: 'red'}}>*</span>
        </label>
        </div>
        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
             <input
            type="text"
            className="form-control"
            placeholder="Enter Prospect Name"
            name="prospect_Name"
            onChange={this.handleChange}
            value={this.state.prospect_Name}/> {this
            .validator
            .message("Prospect Name", this.state.prospect_Name, "required|specialCharText|whitespace|min:2|max:30")}
        <div className="text-danger">
            {" "}
            {this.state.error_message}
        </div>
    </div>
    </div>
    <div className="row m-b-20 class-row">
    <div className="col-lg-2 col-md-2 col-sm-2 col-xs-12">
        <label>Company Name <span style={{color: 'red'}}>*</span>
        </label>
        </div>
        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
             <input
            type="text"
            className="form-control"
            placeholder="Enter Company  Name"
            name="company_Name"
            onChange={this.handleChange}
            value={this.state.company_Name}/> {this
            .validator
            .message("Company Name", this.state.company_Name, "required|specialCharText|whitespace|min:2|max:30")}
        <div className="text-danger">
            {" "}
            {this.state.error_message}
        </div>
    </div>
    </div>
    <div className="row m-b-20 class-row" >
    <div className="col-lg-2 col-md-2 col-sm-2 col-xs-12">
         <label>City<span style={{color: 'red'}}>*</span>
        </label>
        </div>
        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
        <select
        name="select"
         className="form-control"
           name="city"
          onChange={this.handleChange}>
          <option>Select City</option>
           {this.state.City && this.state.City.map((data, index) => {

                   return(
                       <option value={data.city} key={index}>{data.city}</option>
                        )
                                                                 } )}
                                                                </select>
       {this
                .validator
                .message("City ", this.state.city, "required")}
            <div className="text-danger">
                {" "}
                {this.state.error_message}
        </div>
    </div>
    </div>
    <div className="row m-b-20 class-row">
    <div className="col-lg-2 col-md-2 col-sm-2 col-xs-12">
        <label>Contact Number<span style={{color: 'red'}}>*</span>
        </label>
        </div>
        
    <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
        <input
            type="text"
            className="form-control"
            placeholder="Enter Contact Number"
            name="contact_Number"
            onChange={this.handleChange}
            value={this.state.contact_Number}/> {this
                .validator
                .message(" Contact Number", this.state.contact_Number, "required||numeric|max:13")}  <div className="text-danger">
            {" "}
            {this.state.error_message}
        </div>
    </div>
    </div>
    <div className="row m-b-20 class-row">
    <div className="col-lg-2 col-md-2 col-sm-2 col-xs-12">
        <label>E-mail ID<span style={{color: 'red'}}>*</span>
        </label>
        </div>
    <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
        <input
            type="text"
            className="form-control"
            placeholder="Enter E-mail ID"
            name="email"
            id="price_cost"
            onChange={this.handleChange}
            value={this.state.email}/> {this
            .validator
            .message("E-mail Address", this.state.email, "required|email|max:30")}

    </div>
</div>

    
    <div className="row m-b-20 class-row">
    <div className="col-lg-2 col-md-2 col-sm-2 col-xs-12">
        <label>Source of Enquiry<span style={{color: 'red'}}>*</span>
        </label>
        </div>
        
    <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
        <input
            type="text"
            className="form-control"
            placeholder="Enter Source of Enquiry"
            name="source_Of_Enquiry"
            onChange={this.handleChange}
            value={this.state.source_Of_Enquiry}/> {this
            .validator
            .message("Source of Enquiry", this.state.source_Of_Enquiry, "required|specialCharText|whitespace|min:2|max:30")}
        <div className="text-danger">
            {" "}
            {this.state.error_message}
        </div>
    </div>
    </div>


    {/* <input
                                                                                    type="radio"
                                                                                    name="permission_type"
                                                                                    value="None"
                                                                                    onChange={this.handleChange}
                                                                                    checked={this.state.permission_type === 'None'}/>
                                                                                None
                                                                            </label> */}

<div className="row m-b-20 class-row">
    <div className="col-lg-2 col-md-2 col-sm-2 col-xs-12">
        <label>Contacted<span style={{color: 'red'}}>*</span>
        </label>
        </div>
        
    <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
    <label>
    <input
    type="radio"
      name="contacted"
      value="Yes"
       onChange={this.handleChange}
       checked={this.state.contacted === 'None'}/>Yes
       </label>
       <label>
    <input
    type="radio"
      name="contacted"
      value="No"
       onChange={this.handleChange}
       checked={this.state.contacted === 'None'}/>No
       </label>
    </div>
    </div>
    
    <div className="row m-b-20 class-row">
    <div className="col-lg-2 col-md-2 col-sm-2 col-xs-12">
        <label>Email Sent<span style={{color: 'red'}}>*</span>
        </label>
        </div>
        
    <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
    <label>
    <input
    type="radio"
      name="email_Sent"
      value="Yes"
       onChange={this.handleChange}
       checked={this.state.email_Sent === 'None'}/>Yes
       </label>
       <label>
    <input
    type="radio"
      name="email_Sent"
      value="No"
       onChange={this.handleChange}
       checked={this.state.email_Sent === 'None'}/>No
       </label>
    </div>
    </div>
    
    <div className="row m-b-20 class-row">
<div className="col-lg-2 col-md-2 col-sm-2 col-xs-12">
<label>Follow Up Date<span style={{ color: 'red' }}>*</span> </label>
   </div>
    <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
       <textarea
        className="form-control"
         placeholder="Follow up date"
         name="follow_Up_Date"
          style={divStyle}
           onChange={this.handleChange}
           value={this.state.follow_Up_Date}/>
           {this.validator.message("Follow Up Date", this.state.follow_Up_Date, "required")}
              <div className="text-danger">
                {" "}
                 {this.state.error_message}
                </div>
    </div>                                                                                                           
</div>

<div className="row m-b-20 class-row">
<div className="col-lg-2 col-md-2 col-sm-2 col-xs-12">
<label>Follow Up Time<span style={{ color: 'red' }}>*</span> </label>
   </div>
    <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
       <textarea
        className="form-control"
         placeholder="Follow Up Time"
         name="follow_Up_Time"
          style={divStyle}
           onChange={this.handleChange}
           value={this.state.follow_Up_Time}/>
           {this.validator.message("Follow Up Time", this.state.follow_Up_Time, "required")}
              <div className="text-danger">
                {" "}
                 {this.state.error_message}
                </div>
    </div>                                                                                                           
</div>

    <div className="row m-b-20 class-row">
<div className="col-lg-2 col-md-2 col-sm-2 col-xs-12">
<label>Remarks<span style={{ color: 'red' }}>*</span> </label>
   </div>
    <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
       <textarea
        className="form-control"
         placeholder="Remarks"
         name="remarks"
          style={divStyle}
           onChange={this.handleChange}
           value={this.state.remarks}/>
           {this.validator.message("Remarks", this.state.remarks, "required|min:2|max:200")}
              <div className="text-danger">
                {" "}
                 {this.state.error_message}
                </div>
    </div>                                                                                                           
</div>
   




<div className="row">
    <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
        <div className="text-center custom-pro-edt-ds">
            <button
                type="submit"
                className="btn btn-ctl-bt waves-effect waves-light m-r-10">Save
            </button>
            <Link to="/Locations">
                <button type="button" className="btn btn-ctl-bt waves-effect waves-light">Cancel
                </button>
            </Link>
        </div>
    </div>
</div>
</Form>
                                            </div>

                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
                <FooterPage/>

            </div>
        );
    }
}

 export default AddSalesTracker;
