import React from "react";
import firebase from './config';

import {BrowserRouter as Router, Route, Link, Redirect, withRouter} from "react-router-dom";
import Login from "./auth/Login";
import MyProfile from "./component/MyProfile";
import ChangePassword from "./component/changepassword";

import Dashboard from "./component/dashboard";
import EmployeeList from "./component/employee-list";
import AddEmployee from "./component/add_employee";
import EditEmployee from "./component/edit_employee";
import ViewEmployee from "./component/viewemployee_details";

import Locations from "./component/location_list";
import AddLocations from "./component/add_location";
import EditLocation from "./component/edit_location";

import DepartmentList from "./component/department_list";
import AddDepartment from "./component/add_department";
import EditDepartment from "./component/edit_department";

import DesignationList from "./component/designation_list";
import AddDesignation from "./component/add_designation";
import EditDesignation from "./component/edit_designation";


import AddFiles from "./component/add_files";
import EditFile from "./component/edit_file";
 import FileList from "./component/file_list";
 import ViewFile from "./component/viewfile";

export const PrivateRoute = ({
    component: Component,
    ...rest
}) => (
    <Route
        {...rest}
        render={props => sessionStorage.getItem("RoleId")
        ? (<Component {...props}/>)
        : (<Redirect
            to={{
            pathname: "/login",
            state: {
                from: props.location
            }
        }}/>)}/>
);

class Routing extends React.Component {
    constructor(props) {
        super(props);
        this.state = {

            orderStatusList: []
        };
    }
    

    render() {
        return (
            <Router>
                <div className="site-wrapper">
                    <Route exact strict path="/" component={Login}/>

                    <Route path="/login" component={Login}/>
                   
                    <PrivateRoute path="/MyProfile" component={MyProfile}/>
                    <PrivateRoute path="/ChangePassword" component={ChangePassword}/>

                    <PrivateRoute path="/Dashboard" component={Dashboard}/>
                    <PrivateRoute path="/EmployeeList" component={EmployeeList}/>
                    <PrivateRoute path="/AddEmployee" component={AddEmployee}/>
                    <PrivateRoute path="/EditEmployee/:employeeID" component={EditEmployee}/>
                    <PrivateRoute path="/ViewEmployee/:employeeID" component={ViewEmployee}/>

                    <PrivateRoute path="/Locations" component={Locations}/>
                    <PrivateRoute path="/AddLocations" component={AddLocations}/>
                    <PrivateRoute path="/EditLocation/:locationId" component={EditLocation}/>

                    <PrivateRoute path="/Departments" component={DepartmentList}/>
                    <PrivateRoute path="/AddDepartment" component={AddDepartment}/>
                    <PrivateRoute path="/EditDepartment/:locationId" component={EditDepartment}/>

                    <PrivateRoute path="/DesignationList" component={DesignationList}/>
                    <PrivateRoute path="/AddDesignation" component={AddDesignation}/>
                    <PrivateRoute path="/EditDesignation/:locationId" component={EditDesignation}/>
                   
                    <PrivateRoute path="/FileList" component={FileList}/>
                    <PrivateRoute path="/AddFiles/:folderId" component={AddFiles}/>
                    <PrivateRoute path="/EditFile/:fileID" component={EditFile}/>
                    <PrivateRoute path="/ViewFile/:fileID" component={ViewFile}/>

                </div>
            </Router>
        );
    }
}

export default Routing;
