sourcesContent:
import React from "react";
import HeaderPage from "./header";
import FooterPage from "./footer";
import SidebarPage from "./sidebar";
import firebase from '../config';
import ReactQuill from "react-quill";
import 'react-quill/dist/quill.snow.css';
import FileUploader from "react-firebase-file-uploader";
import SimpleReactValidator from "simple-react-validator";
class AddSEOPage extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            pageTitle: '',
            date: new Date().toLocaleString(),
            description: '',
            theme: 'snow',
            avatar: "",
            isUploading: false,
            progress: 0,
            avatarURL: "",
            title: '',
            charset: '',
            keywords: '',
            viewport: '',
            robots: '',
            domainverify: '',
            twittersite: '',
            twittercreator: '',
            twittercard: '',
            twitterplayer: '',
            twittertitle: '',
            twitterimage: '',
            twitterdescription: '',
            geoplacename: '',
            geoposition: '',
            georegion: '',
            ICBM: '',
            oglocale: '',
            ogsitename: '',
            ogtitle: '',
            ogdescription: '',
            ogtype: '',
            ogurl: '',
        }
      
        this.validator = new SimpleReactValidator({
            className: "text-danger",
            validators: {

                whitespace: {
                    message: "The :attribute not allowed first whitespace   characters.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /[^\s\\]/) && params.indexOf(val) === -1);
                    }
                },
                specialChar: {
                    message: "The :attribute not allowed special   characters.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /^[ A-Za-z0-9_@./#&+-]*$/i) && params.indexOf(val) === -1);
                    }
                },
                specialCharText: {
                    message: "The :attribute may only contain letters, dot and spaces.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /^[ A-Za-z_@./#&+-]*$/i) && params.indexOf(val) === -1);
                    }
                },
                url: {
                  message: "The Url should be example.com ",
                  rule: function(val, params, validator) {
                    //return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) && params.indexOf(val) === -1
                    return (
                      validator.helpers.testRegex(
                        val,
                        /(http(s)?:\/\/.)?(www\.)?[-a-zA-Z0-9@:%._\+~#=]{2,256}\.[a-z]{2,6}\b([-a-zA-Z0-9@:%_\+.~#?&//=]*)/g
                      ) && params.indexOf(val) === -1
                    );
                  }
                },
            }
        });

    }
    handleChange(value) {
        this.setState({description: value})
    };
    onChange = (event) => {

        this.setState({
            [event.target.name]: event.target.value
        });
    };
    handleUploadStart = () => this.setState({isUploading: true, progress: 0});
    handleProgress = progress => this.setState({progress});
    handleUploadError = error => {
        this.setState({isUploading: false});
        console.error(error);
    };
    handleUploadSuccess = filename => {
        this.setState({avatar: filename, progress: 100, isUploading: false});
        console.log(this.state.avatar);
        firebase
            .storage()
            .ref("images")
            .child(filename)
            .getDownloadURL()
            .then(url => this.setState({avatarURL: url}));
    };
    handleSubmit = (event) => {
        event.preventDefault();

         if (this.validator.allValid()) {

           
                event.preventDefault();
                this.addNotification();

                let dbCon = firebase
                    .database()
                    .ref('/SEOPages');

                dbCon.push({
                    pageTitle: this.state.pageTitle,
                    title: this.state.title,
                    charset: this.state.charset,
                    keywords: this.state.keywords,
                    description: this.state.description,
                    viewport: this.state.viewport,
                    robots: this.state.robots,
                    domainverify: this.state.domainverify,
                    twittersite: this.state.twittersite,
                    twittercreator: this.state.twittercreator,
                    twittercard: this.state.twittercard,
                    twitterplayer: this.state.twitterplayer,
                    twittertitle: this.state.twittertitle,
                    twitterimage: this.state.twitterimage,
                    twitterdescription: this.state.twitterdescription,
                    geoplacename: this.state.geoplacename,
                    geoposition: this.state.geoposition,
                    georegion: this.state.georegion,
                    ICBM: this.state.ICBM,
                    oglocale: this.state.oglocale,
                    ogsitename: this.state.ogsitename,
                    ogtitle: this.state.ogtitle,
                    ogdescription: this.state.ogdescription,
                    ogtype: this.state.ogtype,
                    ogurl: this.state.ogurl,
                    ogimage: this.state.avatarURL,
                    created_on: this.state.date
                });

                this.setState({});
                this
                    .props
                    .history
                    .push("/SEOPages/Created");
            

