import React from "react";
import {Link} from "react-router-dom";
import HeaderPage from "./header";
import FooterPage from "./footer";
import SidebarPage from "./sidebar";
import firebase from '../config';
import swal from 'sweetalert';
import $ from 'jquery';
import SimpleReactValidator from "simple-react-validator";
import Modal from 'react-responsive-modal';
import FilesList from './folder_select';
import {connect} from 'react-redux';

$.DataTable = require('datatables.net');

class Files extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            folderName:'',
            created_on: new Date().toLocaleString(),
            validError: true,
            loading: false,
            folderList:[],
            Files:[],
            createFolder:'',
            open:false,
           
        };
        this.validator = new SimpleReactValidator({
            className: "text-danger",
            validators: {

                whitespace: {
                    message: "The :attribute not allowed first whitespace   characters.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /[^\s\\]/) && params.indexOf(val) === -1);
                    }
                },
                specialChar: {
                    message: "The :attribute not allowed special   characters.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /^[ A-Za-z0-9_@./#&+-]*$/i) && params.indexOf(val) === -1);
                    }
                },
                specialCharText: {
                    message: "The :attribute may only contain letters, dot and spaces.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /^[ A-Za-z_@./#&+-]*$/i) && params.indexOf(val) === -1);
                    }
                }
            }
        });

    }
    
    componentDidMount() {
        var ref = firebase
            .database()
            .ref('Files/');
        ref.once("value", snapshot => {
            const data = [];
            console.log(snapshot.val());
            snapshot.forEach(element => {
                const usersData = {
                    fileID: element
                        .key
                        .toString(),

                    file: element
                        .val()
                        .file,
                    name: element
                        .val()
                        .name,
                        folderName: element
                        .val()
                        .folderName,

                    created_on: element
                        .val()
                        .created_on
                }
                data.unshift(usersData);
            });
            console.log(data);
            this.setState({
                Files: data,
                createFolder:data[0].folderName
            }, () => {
                console.log(this.state.createFolder, 'Files');
            });
             this.props.dispatch({
            type:'ADD_POST',
            data,
            
          });
        })

       
          
        this.folderListView();
    }

    folderListView() {
        var ref = firebase
            .database()
            .ref('folder/');
        ref.once("value", snapshot => {
            const data = [];
            console.log(snapshot.val());
            snapshot.forEach(element => {
                const usersData = {
                    fileID: element
                        .key
                        .toString(),

                        folderName: element
                        .val()
                        .folderName,
                   

                    created_on: element
                        .val()
                        .created_on
                }
                data.push(usersData);
            });

            this.setState({
                folderList: data,
                
            }, () => {
                console.log(this.state.folderList, 'Files');
            });
        })
    }
    onOpenModal = () => {
       
        this.setState({open: true});
    };

    onCloseModal = () => {
        this.setState({open: false});
    };
    // componentWillUnmount() {

    //     clearTimeout(this.interval);
    // }
    deleteItem = id => {

        swal({title: "Are you sure?", text: "Do your really want to remove?", icon: "warning", buttons: true, dangerMode: true}).then(willDelete => {
            if (willDelete) {
                console.log(id);
                var playersRef = firebase
                    .database()
                    .ref(`/Files/${id}`);
                playersRef.remove();

                this
                    .props
                    .history
                    .push("/Files/Deleted");
            } else {}

        });

    };
    handleSubmit = (event) => {
        event.preventDefault();
        if (this.validator.allValid() && this.state.validError == true) {
            console.log(this.state.avatar);
             var sessionId = sessionStorage.getItem("RoleId");
             var userName = sessionStorage.getItem("userName");

            let dbCon = firebase
                .database()
                .ref('/folder');
            dbCon.push({

                folderName: this.state.folderName,
                userId: sessionId,
                userName: userName,
               
                created_on: this.state.created_on
            });

            this.onCloseModal();
               
            this
                .props
                .history
                .push("/FileList/Created");

                // this.componentDidMount();

        } else {
            this
                .validator
                .showMessages();
            this.forceUpdate();
        }

    };
    handleChange = (event) => {
        this.setState({
            [event.target.name]: event.target.value
        });
    };


    handleClick =(id)=>{
    
        this.setState({
           
            createFolder:id
        }, () => {
            console.log(this.state.createFolder, 'createFolder');
        });
    }
    render() {

        var pathname = this     .props     .location     .pathname     .split("/");
        var alertName = pathname[2];
        const { open} = this.state;
        return (
            <div>
                <SidebarPage/>

                <div className="all-content-wrapper">
                    <HeaderPage/>

                    <div className="breadcome-area">
                        <div className="container-fluid">
                            <div className="row">
                                <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                    <div className="breadcome-list">
                                        <div className="row">
                                            <div className="col-lg-6 col-md-6 col-sm-6 col-xs-6">
                                                <div className="breadcomb-wp">

                                                    <div className="breadcomb-ctn">
                                                        <h2>Files</h2>

                                                    </div>

                                                </div>
                                            </div>

                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                    <div className="product-status mg-b-30">
                        <div className="container-fluid">
                            <div className="row">
                                <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                    <div className="product-status-wrap">
                                        <h4>Files List</h4>

                                        <div className="row">
                                            <div className="col-lg-3 col-md-3 col-sm-4">
                                                <div className="folderblock">
                                                    <div className="folderhead" onClick={this.onOpenModal}>Folders
                                                        <span className="pull-right" data-toggle="modal" data-target="#exampleModal">
                                                            <i className="fa fa-plus-circle" aria-hidden="true"></i>
                                                        </span>
                                                    </div>
                                                    {this.state.folderList && this
                                                            .state
                                                            .folderList
                                                            .map((data, index) => {
                                                              

                                                                return (
                                                    <div className="folderrow" key={index}  onClick={()=>this.handleClick(data.folderName)}>
                                                        <i className="fa fa-folder-open" aria-hidden="true"></i>
                                                       {data.folderName}</div>)})}
                                                   
                                                </div>
                                            </div>
                                            <p>{this.state.createFolder}</p>

                                            <FilesList createFolder={this.state.createFolder}/>

                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                </div>

                <FooterPage/>
                {open? <Modal open={open} onClose={this.onCloseModal} center>
                    <div className="modal-dialog" role="document">
                      
                            <form onSubmit={this.handleSubmit}>
                                <div className="modal-header">
                                    <h5 className="modal-title" id="exampleModalLabel">Create Folder</h5>
                                   
                                </div>
                                <div className="modal-body">
                                    <input
                                        type="text"
                                        className="form-control"
                                        placeholder="Enter Folder Name"
                                        name="folderName"
                                        onChange={this.handleChange}
                                        value={this.state.folderName}/> {this
                                        .validator
                                        .message("Folder Name", this.state.folderName, "required|whitespace|min:2|max:70")}

                                </div>
                                <div className="modal-footer">
                                    <button type="button" className="btn btn-secondary" data-dismiss="modal" onClick={this.onCloseModal}>Cancel</button>
                                    <button type="submit" className="btn btn-primary">Save</button>
                                </div>
                            </form>
                        </div>
                  
                    </Modal>:''}

                {/* <div
                    className="modal fade"
                    id="exampleModal"
                    tabindex="-1"
                    role="dialog"
                    aria-labelledby="exampleModalLabel"
                    aria-hidden="true">
                    <div className="modal-dialog" role="document">
                        <div className="modal-content">
                            <form onSubmit={this.handleSubmit}>
                                <div className="modal-header">
                                    <h5 className="modal-title" id="exampleModalLabel">Create Folder</h5>
                                    <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                                        <span aria-hidden="true">&times;</span>
                                    </button>
                                </div>
                                <div className="modal-body">
                                    <input
                                        type="text"
                                        className="form-control"
                                        placeholder="Enter Folder Name"
                                        name="folderName"
                                        onChange={this.handleChange}
                                        value={this.state.folderName}/> {this
                                        .validator
                                        .message("Folder Name", this.state.folderName, "required|whitespace|min:2|max:70")}

