import React from "react";
import {Link} from "react-router-dom";

import {Form, Input, Button, Alert} from 'reactstrap';
import HeaderPage from "./header";
import FooterPage from "./footer";
// import HomePage from "./home";
import SidebarPage from "./sidebar";
import firebase from '../config';
import ReactNotification from "react-notifications-component";
import "react-notifications-component/dist/theme.css";
import SimpleReactValidator from "simple-react-validator";

class AddRolePage extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            Role: '',
            date: new Date().toLocaleString()
        }
        this.addNotification = this
            .addNotification
            .bind(this);
        this.notificationDOMRef = React.createRef();
        this.validator = new SimpleReactValidator({
            className: "text-danger",
            validators: {

                whitespace: {
                    message: "The :attribute not allowed first whitespace   characters.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /[^\s\\]/) && params.indexOf(val) === -1);
                    }
                },
                specialChar: {
                    message: "The :attribute not allowed special   characters.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /^[ A-Za-z0-9_@./#&+-]*$/i) && params.indexOf(val) === -1);
                    }
                },
                specialCharText: {
                    message: "The :attribute may only contain letters, dot and spaces.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /^[ A-Za-z_@./#&+-]*$/i) && params.indexOf(val) === -1);
                    }
                }
            }
        });

    }
    // componentDidMount(){   this.addNotification(); }
    onChange = (event) => {

        this.setState({
            [event.target.name]: event.target.value
        });
    };

    handleSubmit = (event) => {
        event.preventDefault();
        if (this.validator.allValid()) {
            if (this.state.Role && this.state.Role.trim().length !== 0) {
                this.addNotification();

                event.preventDefault();
                let dbCon = firebase
                    .database()
                    .ref('/Role');
                dbCon
                    .push({Role: this.state.Role, created_on: this.state.date})
                    .then(response => {
                        try {
                            console.log(response);
                            this.setState({Role: ''});
                            this
                                .props
                                .history
                                .push("/RoleListPage");

                        } catch (error) {
                            console.log('no');
                        }
                    });
            }
        } else {
            this
                .validator
                .showMessages();
            this.forceUpdate();
        }

    };

    addNotification() {
        this
            .notificationDOMRef
            .current
            .addNotification({
                title: "Success",
                message: "Succesfully added!",
                type: "success",
                insert: "bottom",
                container: "top-center",
                animationIn: [
                    "animated", "fadeIn"
                ],
                animationOut: [
                    "animated", "fadeOut"
                ],
                dismiss: {
                    duration: 2000
                },
                dismissable: {
                    click: true
                }
            });
    }

    render() {

        return (
            <div>
                <SidebarPage/>

                <div className="all-content-wrapper">
                    <HeaderPage/>

                    <div className="breadcome-area">
                        <div className="container-fluid">
                            <div className="row">
                                <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                    <div className="breadcome-list">
                                        <div className="row">
                                            <div className="col-lg-6 col-md-6 col-sm-6 col-xs-6">
                                                <div className="breadcomb-wp">

                                                    <div className="breadcomb-ctn">
                                                        <h2>Role</h2>
                                                    </div>
                                                </div>
                                            </div>

                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                    <div className="single-product-tab-area mg-b-30">

                        <div className="single-pro-review-area">
                            <div className="container-fluid">
                                <div className="row">
                                    <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                        <div className="review-tab-pro-inner">

                                            <h4>Add Role</h4>
                                            <ReactNotification ref={this.notificationDOMRef}/>

                                            <div className="product-tab-list tab-pane fade active in">
                                                <form onSubmit={this.handleSubmit} id="Add-category">

                                                    <div className="row m-b-20">
                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <label >Role<span style={{
                color: 'red'
            }}>*</span>
                                                            </label>
                                                            <input
                                                                type="text"
                                                                className="form-control"
                                                                placeholder="Role Value*"
                                                                id="add_Role"
                                                                name="Role"
                                                                onChange={this.onChange}
                                                                value={this.state.Role}/> {this
                                                                .validator
                                                                .message("Role Value", this.state.Role, "required|whitespace|specialChar|min:3|max:70")}

                                                        </div>

                                                    </div>

                                                    <div className="row">
                                            <div className="col-lg-6 col-md-6 col-sm-12 col-xs-12">
                                                <div className="text-left custom-pro-edt-ds">

          <button type="submit" className="btn btn-ctl-bt waves-effect waves-light">Add</button>
          <Link to="/PincodeList">
                                                                    <button type="button" className="btn btn-ctl-bt waves-effect waves-light">Cancel
                                                                    </button>
                                                                </Link>

                                                  
                                                </div>
                                            </div>
                                        </div>
                                                </form>
                                            </div>

                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
                <FooterPage/>

            </div>
        );
    }
}

export default AddRolePage;
