import React from "react";
import HeaderPage from "./header";
import FooterPage from "./footer";
import SidebarPage from "./sidebar";
import firebase from '../config';
import {Form} from 'reactstrap';

import SimpleReactValidator from "simple-react-validator";

const getCountriesList = require('countrycitystatejson');
// console.log(getCountriesList.getCountries());
// console.log(getCountriesList.getStatesByShort("IN"));

class AddMerchantPage extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            merchantName: '',
            contactNumber: '',
            emailId: '',
            countryName: getCountriesList.getCountries(),
            stateName: [],
            cityName: [],
            selectedCountry: '',
            selectedCountryName: '',
            selectedstateName: '',
            selectedcityName: '',
            pincode: '',
            address: '',
            latitude: '',
            longitude: '',
            location: '',
            shopType: '',
            branches: '',
            radius: '',
            startTime: "",
            endTime: false,
            gstValue: 0,
            serviceCharge: "",
            deliveryFee: 0,
            minOrder: "",
            uploadImage: '',
            date: new Date().toLocaleString()
        }

        this.validator = new SimpleReactValidator({
            className: "text-danger",
            validators: {
                passwordvalid: {
                    message: "The :attribute must be at least 6 and at most 30 with 1 numeric,1 special charac" +
                            "ter and 1 alphabet.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /^(?=.*\d)(?=.*[!@#$%^&*])(?=.*[a-z]).{6,30}$/i) && params.indexOf(val) === -1);
                    }
                },
                passwordMismatch: {
                    message: "confirm password must match with password field.",
                    rule: function (val, params, validator) {
                        return document
                            .getElementById("password_input")
                            .value === val
                            ? true
                            : false;
                    }
                },
                whitespace: {
                    message: "The :attribute not allowed first whitespace   characters.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /[^\s\\]/) && params.indexOf(val) === -1);
                    }
                },
                specialChar: {
                    message: "The :attribute not allowed special   characters.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /^[ A-Za-z0-9_@./#&+-]*$/i) && params.indexOf(val) === -1);
                    }
                },
                specialCharText: {
                    message: "The :attribute may only contain letters, dot and spaces.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /^[ A-Za-z_@./#&+-]*$/i) && params.indexOf(val) === -1);
                    }
                },

                // zip: {   message: "Invalid zip code",   rule: function(val, params,
                // validator) {     //return
                // validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) && params.indexOf(val)
                // === -1     return (       validator.helpers.testRegex(val,
                // /^(\d{5}(\d{4})?)?$/i) &&       params.indexOf(val) === -1     );   } },
                website: {
                    message: "The Url should be example.com ",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /(http(s)?:\/\/.)?(www\.)?[-a-zA-Z0-9@:%._\+~#=]{2,256}\.[a-z]{2,6}\b([-a-zA-Z0-9@:%_\+.~#?&//=]*)/g) && params.indexOf(val) === -1);
                    }
                },
                Fax: {
                    message: "Invalid fax number ",
                    rule: function (val, params, validator) {
                        return (validator.helpers.testRegex(val, /^([\w-\.]+)@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.)|(([\w-]+\.)+))([a-zA-Z]{2,4}|[0-9]{1,3})(\]?)$/i) && params.indexOf(val) === -1);
                    }
                }
            }
        });
    }

    componentDidMount() {
        this.setState({loading: true});

    }

    onChange = (event) => {

        this.setState({
            [event.target.name]: event.target.value
        });
    };
    setCountryName = (e) => {
        let index = e.target.selectedIndex;
        let el = e.target.childNodes[index]
        let option = el.getAttribute('id');
        let selectedCountryName = e.target.value;

        const statesList = getCountriesList.getStatesByShort(option);
        // console.log(statesList);

        this.setState({
            stateName: statesList,
            selectedCountry: option,
            selectedCountryName
        }, () => {
            console.log(this.state.stateName, 'stateName');
        });

        //console.log(this.state.stateName);

    };
    setStateName = (e) => {
        let stateValue = e.target.value;
        let option = this.state.selectedCountry;
        // console.log(option);
        const cityList = getCountriesList.getCities(option, stateValue);
        // console.log(cityList);
        this.setState({
            cityName: cityList,
            selectedstateName: stateValue
        }, () => {
            console.log(this.state.cityName, 'stateName');
        });

        //console.log(this.state.stateName);

    };
    setStateName = (e) => {
        let stateValue = e.target.value;
        let option = this.state.selectedCountry;
        // console.log(option);
        const cityList = getCountriesList.getCities(option, stateValue);
        // console.log(cityList);
        this.setState({
            cityName: cityList,
            selectedstateName: stateValue
        }, () => {
            console.log(this.state.cityName, 'cityName');
        });

        //console.log(this.state.stateName);

    };
    setCityName = (e) => {
        let cityValue = e.target.value;

        this.setState({
            selectedcityName: cityValue
        }, () => {
            console.log(this.state.selectedcityName, 'selectedcityName');
        });

        //console.log(this.state.stateName);

    };

    handleSubmit = (event) => {
        event.preventDefault();
        if (this.validator.allValid()) {

            let dbCon = firebase
                .database()
                .ref('/merchant');
            dbCon.push({
                merchantName: this.state.merchantName,
                contactNumber: this.state.contactNumber,
                emailId: this.state.emailId,
                countryName: this.state.selectedCountryName,
                stateName: this.state.selectedstateName,
                cityName: this.state.selectedcityName,
                pincode: this.state.pincode,
                address: this.state.address,
                latitude: this.state.latitude,
                longitude: this.state.longitude,
                location: this.state.location,
                shopType: this.state.shopType,
                branches: this.state.branches,
                radius: this.state.radius,
                startTime: this.state.startTime,
                endTime: this.state.endTime,
                gstValue: this.state.gstValue,
                serviceCharge: this.state.serviceCharge,
                deliveryFee: this.state.deliveryFee,
                minOrder: this.state.minOrder,
                uploadImage: this.state.uploadImage,
                created_on: this.state.date
            });
            this
                .props
                .history
                .push("/MerchantListPage");
        } else {
            this
                .validator
                .showMessages();
            this.forceUpdate();
        }

    };

    render() {

        return (
            <div>
                <SidebarPage/>

                <div className="all-content-wrapper">
                    <HeaderPage/>

                    <div className="breadcome-area">
                        <div className="container-fluid">
                            <div className="row">
                                <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                    <div className="breadcome-list">
                                        <div className="row">
                                            <div className="col-lg-6 col-md-6 col-sm-6 col-xs-6">
                                                <div className="breadcomb-wp">

                                                    <div className="breadcomb-ctn">
                                                        <h2>Merchants</h2>
                                                    </div>
                                                </div>
                                            </div>

                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                    <div className="single-product-tab-area mg-b-30">

                        <div className="single-pro-review-area">
                            <div className="container-fluid">
                                <div className="row">
                                    <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                        <div className="review-tab-pro-inner">

                                            <h4>Add Merchant</h4>

                                            <div className="product-tab-list tab-pane fade active in">
                                                <Form onSubmit={this.handleSubmit} id="Add-products">

                                                    <div className="row m-b-20">
                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <label>Merchant Name</label>
                                                            <input
                                                                type="text"
                                                                className="form-control"
                                                                placeholder=""
                                                                name="merchantName"
                                                                onChange={this.onChange}
                                                                value={this.state.merchantName}/> {this
                                                                .validator
                                                                .message("Merchant Name", this.state.merchantName, "required|specialCharText|whitespace|min:2|max:70")}
                                                        </div>

                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <label>Contact Number</label>
                                                            <input
                                                                type="text"
                                                                className="form-control"
                                                                placeholder=""
                                                                name="contactNumber"
                                                                onChange={this.onChange}
                                                                value={this.state.contactNumber}/> {this
                                                                .validator
                                                                .message("Contact Number", this.state.contactNumber, "required|numeric|min:4|max:15")}
                                                        </div>
                                                    </div>

                                                    <div className="row m-b-20">
                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <label>Email ID</label>
                                                            <input
                                                                type="text"
                                                                className="form-control"
                                                                placeholder=""
                                                                name="emailId"
                                                                onChange={this.onChange}
                                                                value={this.state.emailId}/> {this
                                                                .validator
                                                                .message("Email ID", this.state.emailId, "required|email|min:6|max:70")}

                                                        </div>

                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <label>Select Country</label>
                                                            <select
                                                                name="select"
                                                                className="form-control pro-edt-select form-control-primary"
                                                                name="Country"
                                                                onChange={this.setCountryName}>

                                                                <option >Select Country</option>
                                                                {this
                                                                    .state
                                                                    .countryName
                                                                    .map((data, index) => {

                                                                        return (
                                                                            <option value={data.name} id={data.shortName} key={index}>{data.name}</option>
                                                                        )
                                                                        {
                                                                            this
                                                                                .validator
                                                                                .message("Country", data.name, "required")
                                                                        }

                                                                    })}

                                                            </select>

                                                        </div>
                                                    </div>

                                                    <div className="row m-b-20">
                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <label>Select State</label>
                                                            <select
                                                                name="select"
                                                                className="form-control pro-edt-select form-control-primary"
                                                                name="name"
                                                                onChange={this.setStateName}>

                                                                <option value="">Select State Name</option>
                                                                {this.state.stateName && this
                                                                    .state
                                                                    .stateName
                                                                    .map((data) => {

                                                                        return (
                                                                            <option value={data} key={data}>{data}</option>
                                                                        )
                                                                        {
                                                                            this
                                                                                .validator
                                                                                .message("State", data, "required")
                                                                        }

                                                                    })}
                                                            </select>

                                                        </div>

                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <label>City Name</label>
                                                            <select
                                                                name="selectCityName"
                                                                className="form-control pro-edt-select form-control-primary"
                                                                name="name"
                                                                onChange={this.setCityName}>

                                                                <option value="">Select City Name</option>
                                                                {this.state.cityName && this
                                                                    .state
                                                                    .cityName
                                                                    .map((data) => {

                                                                        return (
                                                                            <option value={data} key={data}>{data}</option>
                                                                        )
                                                                        {
                                                                            this
                                                                                .validator
                                                                                .message("Country", data, "required")
                                                                        }

                                                                    })}
                                                            </select>

                                                            {/* <input type="text" className="form-control" placeholder=""/> */}

                                                        </div>
                                                    </div>
                                                    <div className="row m-b-20">
                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <label>Pincode</label>
                                                            <input
                                                                type="text"
                                                                className="form-control"
                                                                placeholder=""
                                                                name="pincode"
                                                                onChange={this.onChange}
                                                                value={this.state.pincode}/> {this
                                                                .validator
                                                                .message("ZIP Code", this.state.zip, "required|min:5|max:6")}

                                                        </div>

                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <label>Address</label>
                                                            <input
                                                                type="text"
                                                                className="form-control"
                                                                placeholder=""
                                                                name="address"
                                                                onChange={this.onChange}
                                                                value={this.state.address}/> {this
                                                                .validator
                                                                .message("Address", this.state.address, "required|min:3|max:255")}
                                                        </div>
                                                    </div>

                                                    <hr/>

                                                    <div className="row m-b-20">
                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <h4>Set Location
                                                                <span
                                                                    style={{
                                                                    fontSize: "12px",
                                                                    color: "red"
                                                                }}>Drop Pin to your location on map
                                                                </span>
                                                            </h4>
                                                            <iframe
                                                                src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d487295.02532044525!2d78.12785129924684!3d17.41215307568293!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x3bcb99daeaebd2c7%3A0xae93b78392bafbc2!2sHyderabad%2C+Telangana!5e0!3m2!1sen!2sin!4v1555583258581!5m2!1sen!2sin"
                                                                width="100%"
                                                                height="450"
                                                                frameBorder="0"
                                                                style={{
                                                                border: "0"
                                                            }}
                                                                allowFullScreen></iframe>
                                                        </div>

                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12 m-t-28">
                                                            <div className="row m-b-20">
                                                                <span className="col-md-6">
                                                                    <label>Latitude</label>
                                                                    <input
                                                                        type="text"
                                                                        className="form-control"
                                                                        placeholder=""
                                                                        name="latitude"
                                                                        onChange={this.onChange}
                                                                        value={this.state.latitude}/></span>
                                                                <span className="col-md-6">
                                                                    <label>Longitude</label>
                                                                    <input
                                                                        type="text"
                                                                        className="form-control"
                                                                        placeholder=""
                                                                        name="longitude"
                                                                        onChange={this.onChange}
                                                                        value={this.state.longitude}/></span>
                                                            </div>

                                                            <div className="row m-b-20">

                                                                <div className="col-md-12">
                                                                    <label>Location</label><input
                                                                        type="text"
                                                                        className="form-control"
                                                                        placeholder=""
                                                                        name="location"
                                                                        onChange={this.onChange}
                                                                        value={this.state.location}/> {this
                                                                        .validator
                                                                        .message("location", this.state.location, "required")}</div>
                                                            </div>

                                                            <div className="row m-b-20">
                                                                <div className="col-md-12">

                                                                    <label>Select Shop type</label>

                                                                    <select
                                                                        name="shopType"
                                                                        className="form-control pro-edt-select form-control-primary"
                                                                        onChange={this.onChange}>
                                                                        <option value="">Select Shop type</option>
                                                                        <option value="Grocery">Grocery</option>
                                                                        <option value="Medical">Medical
                                                                        </option>
                                                                        <option value="Books&Stationary">Books &amp; Stationary
                                                                        </option>
                                                                        <option value="Bakery">Bakery</option>
                                                                        <option value="Tiffin Center">Tiffin Center</option>
                                                                        <option value="Restaurant">Restaurant
                                                                        </option>
                                                                        <option value="Sweets">Sweets</option>
                                                                        <option value="Shoes">Shoes</option>
                                                                        <option value="Gifts&Toys">Gifts &amp; Toys</option>
                                                                        <option value="Florist">Florist</option>
                                                                        <option value="Fashion&Clothing">Fashion &amp; Clothing</option>
                                                                        <option value="Electronics">Electronics
                                                                        </option>
                                                                        <option value="Jewellery">Jewellery</option>
                                                                        <option value="Furniture&Decor">Furniture &amp; Decor</option>
                                                                        <option value="Fruits&Vegetables">Fruits &amp; Vegetables</option>
                                                                        <option value="Automobiles">Automobiles</option>
                                                                        <option value="Cosmetics">Cosmetics</option>
                                                                        <option value="Meat">Meat</option>
                                                                        <option value="Other">Other</option>
                                                                    </select>

                                                                </div>
                                                            </div>

                                                            <div className="row m-b-20">
                                                                <div
                                                                    className="col-md-2"
                                                                    style={{
                                                                    fontSize: "15px",
                                                                    color: "#000"
                                                                }}>Branches:</div>
                                                                <div className="col-md-9">
                                                                    <label className="checkbox-inline">
                                                                        <input
                                                                            type="radio"
                                                                            value=""
                                                                            name="branches"
                                                                            onChange={this.onChange}
                                                                            value={this.state.branches}/>
                                                                        <span className="label-text">Single</span>
                                                                    </label>
                                                                    <label className="checkbox-inline">
                                                                        <input
                                                                            type="radio"
                                                                            value=""
                                                                            name="branches"
                                                                            onChange={this.onChange}
                                                                            value={this.state.branches}/>
                                                                        <span className="label-text">Multiple</span>
                                                                    </label>
                                                                </div>

                                                            </div>

                                                            <div className="row m-b-20">
                                                                <div
                                                                    className="col-md-2"
                                                                    style={{
                                                                    fontSize: "15px",
                                                                    color: "#000"
                                                                }}>Radius:</div>
                                                                <div className="col-md-10">
                                                                    <input
                                                                        type="text"
                                                                        className="form-control"
                                                                        name="radius"
                                                                        onChange={this.onChange}
                                                                        value={this.state.radius}
                                                                        placeholder="Enter Radius Value"/>
                                                                </div>

                                                            </div>

                                                            <div className="row m-b-20">
                                                                <div
                                                                    className="col-md-2 line-40"
                                                                    style={{
                                                                    fontSize: "15px",
                                                                    color: "#000"
                                                                }}>Timings:</div>
                                                                <div className="col-md-10">
                                                                    <label className="checkbox-inline no-padd">
                                                                        <span
                                                                            style={{
                                                                            float: "left"
                                                                        }}>
                                                                            <select
                                                                                name="startTime"
                                                                                onChange={this.onChange}
                                                                                className="form-control pro-edt-select form-control-primary">
                                                                                <option value="">Start Time</option>
                                                                                <option value="1">1</option>
                                                                                <option value="1">2</option>
                                                                                <option value="1">3</option>
