import React from "react";
import {Link} from "react-router-dom";
import firebase from '../config';
import swal from 'sweetalert';
// import $ from 'jquery';

// $.DataTable = require('datatables.net');
class FilesList extends React.Component {
    
    constructor(props) {
        super(props);
        console.log(this.props.createFolder);
        this.state = {
            folderName:this.props.createFolder,
            created_on: new Date().toLocaleString(),
           
            folderList:[],
            Files:[],
           
        };
        

    }
    componentDidMount() {
        setTimeout(() => {
            console.log(this.props.folderName);
          }, 5000);
    }

    // componentDidMount() {


    //     console.log(this.state.folderName);
    //     var ref = firebase
    //         .database()
    //         .ref('Files/');
    //     ref.once("value", snapshot => {
    //         const data = [];
    //         console.log(snapshot.val());
    //         snapshot.forEach(element => {
    //             const usersData = {
    //                 fileID: element
    //                     .key
    //                     .toString(),

    //                 file: element
    //                     .val()
    //                     .file,
    //                 name: element
    //                     .val()
    //                     .name,

    //                 created_on: element
    //                     .val()
    //                     .created_on
    //             }
    //             data.unshift(usersData);
    //         });

    //         this.setState({
    //             Files: data
    //         }, () => {
    //             console.log(this.state.Files, 'Files');
    //         });
    //     })
       
    // }
    componentWillReceiveProps({folderName}) {

        console.log(this.state.folderName);
        // this.setState({...this.state,someProp})
      }
   
    // componentWillUnmount() {

    //     clearTimeout(this.interval);
    // }
    deleteItem = id => {

        swal({title: "Are you sure?", text: "Do your really want to remove?", icon: "warning", buttons: true, dangerMode: true}).then(willDelete => {
            if (willDelete) {
                console.log(id);
                var playersRef = firebase
                    .database()
                    .ref(`/Files/${id}`);
                playersRef.remove();

                this
                    .props
                    .history
                    .push("/Files/Deleted");
            } else {}

        });

    };
    
   
    render() {

       
        return (
            <div className="col-lg-9 col-md-9 col-sm-8">
            <div className="folderheading">{this.props.createFolder}
                <span className="pull-right">

                    <div className="uploadfile">
                        <Link to='/AddFiles'>Add File</Link>
                    </div>
                </span>
            </div>

            
    {/* {alertName
        ? <div
                className="alert alert-success display-none"
                style={{
                display: "block"
            }}>
                <a data-dismiss="alert" href="#" aria-hidden="true" className="close">Ã—</a>
                Files {alertName}

                Successfully
            </div>
        : ''} */}
            <table className="table table-bordered paginationTable">
                <thead>
                    <tr>
                        <th>File Name</th>
                        <th>uploaded By</th>
                        <th>uploaded On</th>
                        <th>Action</th>

                    </tr>
                </thead>
                <tbody>

                    {this.state.Files && this
                        .state
                        .Files
                        .map((data, index) => {
                            console.log(data);

                            return (
                                <tr key={index}>

                                    <td>Lorem Ipsum is simply dummy text of the printing.</td>
                                    <td>{data.name}
                                    </td>
                                    <td>{data.created_on}
                                    </td>
                                    <td>

                                        <Link to={`/EditFile/${data.fileID}`}>
                                            <button
                                                data-toggle="tooltip"
                                                title="Edit"
                                                className="btn-folder btn btn-primary">
                                                <i className="fa fa-pencil-square-o" aria-hidden="true"></i>
                                            </button>
                                        </Link>

                                        <button
                                            data-toggle="tooltip"
                                            title="Trash"
                                            className="btn-folder btn btn-danger"
                                            data-id={data.fileID}
                                            onClick={this
                                            .deleteItem
                                            .bind(this, data.fileID)}>
                                            <i className="fa fa-trash-o" aria-hidden="true"></i>
                                        </button>
                                    </td>

                                </tr>
                            )

                        })}

                </tbody>
            </table>
        </div>
        );
    }
}

export default FilesList;
