import React from "react";

import { Form } from 'reactstrap';
import HeaderPage from "./header";
import FooterPage from "./footer";
import SidebarPage from "./sidebar";
import firebase from '../config';
import ReactQuill from "react-quill";
import 'react-quill/dist/quill.snow.css';



class EditSTAFF extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      fullName:'',
      email:'',
      username:'',
      password:'',
      status:'',
      date:new Date().toLocaleString(),
      theme: 'snow'

  }
  this.onChange = this.onChange.bind(this);
  
  }
  componentDidMount(){
      const {CMSID}=this.props.match.params;
      
      var ref = firebase.database().ref(`staff/${CMSID}`);
      ref.on('value', snapshot => {
            var gstData = snapshot.val();
            //console.log(gstData)
            this.setState({
              fullName: gstData.fullName,
              email: gstData.email,
              username: gstData.username,
              password: gstData.password,
              status: gstData.status,
              createdAt: gstData.createdAt,
              loading: false
            });
            //console.log(this.state.pageTitle);
          });
     
  }
  onChange = (event) => {
      
    this.setState({ [event.target.name]: event.target.value });
  };

 
  handleSubmit = (event) => {
    const {CMSID}=this.props.match.params;
    if (this.state.fullName && this.state.fullName.trim().length !== 0) {
        event.preventDefault();
        let dbCon = firebase.database().ref(`/staff/${CMSID}`);
        console.log(dbCon);
        dbCon.update ({
          fullName: this.state.fullName,
          email: this.state.email,
          username: this.state.username,
          password: this.state.password,
          status: this.state.status,
          updatedAt: this.state.date
        });
       this.setState({});
      this.props.history.push("/staff");
    }
  };
  
  render() {
    
    const divStyle = {
      'height':'300px'
    };

    return (
        <div>
       <SidebarPage/>
     
      <div className="all-content-wrapper">
      <HeaderPage/>
     
            
              <div className="breadcome-area">
                  <div className="container-fluid">
                      <div className="row">
                          <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                              <div className="breadcome-list">
                                  <div className="row">
                                      <div className="col-lg-6 col-md-6 col-sm-6 col-xs-6">
                                          <div className="breadcomb-wp">
                                          
                                              <div className="breadcomb-ctn">
                                              <h2>Admin Staff</h2>
                                                  </div>
                                          </div>
                                      </div>
                            
                                  </div>
                              </div>
                          </div>
                      </div>
                  </div>
              </div>
        
       
              <div className="single-product-tab-area mg-b-30">
            
              <div className="single-pro-review-area">
                  <div className="container-fluid">
                      <div className="row">
                          <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                              <div className="review-tab-pro-inner">
                     
                     <h4>Edit Staff</h4>
                     
                     
                              
                              <div className="product-tab-list tab-pane fade active in">
                              <form className="form-horizontal"  onSubmit={this.handleSubmit} role="form">
          <div className="box-body">
          <div className="form-group">
              <label htmlFor="inputEmail3" className="col-sm-2 control-label">Full Name</label>
              <div className="col-sm-10">
              <input type="text" className="form-control" placeholder="Full Name"  id="fullName"
                            name="fullName" onChange={ this.onChange } value={ this.state.fullName } />
                           
                <span style={{color: 'red'}} /> </div>
            </div>
            <div className="form-group">
              <label htmlFor="inputEmail3" className="col-sm-2 control-label">Email</label>
              <div className="col-sm-10">
                <input type="email" name="email" id="email" onChange={ this.onChange } value={ this.state.email } className="required form-control"  />
                <span style={{color: 'red'}} /> </div>
            </div>
            <div className="form-group">
              <label htmlFor="inputEmail3" className="col-sm-2 control-label">username</label>
              <div className="col-sm-10">
                <input type="text" name="username" id="username" onChange={ this.onChange } value={ this.state.username } className="required form-control"  />
                <span style={{color: 'red'}} /> </div>
            </div>
            <div className="form-group">
              <label htmlFor="inputEmail3" className="col-sm-2 control-label">Password</label>
              <div className="col-sm-10">
                <input type="text" name="password" id="password" onChange={ this.onChange } value={ this.state.password } className="required form-control"  />
                <span style={{color: 'red'}} /> </div>
            </div>
           
            <div className="form-group">
              <label htmlFor="inputEmail3" className="col-sm-2 control-label">status</label>
              <div className="col-sm-10">
              <label className="radio-inline no-padd">
              <input type="radio" name="status" value="1"  onChange={ this.onChange } checked={this.state.usageType === '1'} className="icheck" />
              Active &nbsp;&nbsp;</label>
            <label className="radio-inline no-padd">
              <input type="radio" name="status" value="0"  onChange={ this.onChange } checked={this.state.usageType === '0'}  className="icheck" />
              Active</label>   <span style={{color: 'red'}} /> </div>
            </div>
            
           
          </div>
          <div className="row">
          <div className="col-lg-6 col-md-6 col-sm-12 col-xs-12">
           <div className="text-left custom-pro-edt-ds">
                                                      <button type="submit" className="btn btn-ctl-bt waves-effect waves-light">Save
                                                          </button>
                                                   
                                                  </div>
                                              </div>
                                          </div>
        </form>
           
                                      </div>
                                   
  
              
                                  
                          
                              </div>
                          </div>
                      </div>
                  </div>
              </div>
          </div>
          </div>
           <FooterPage/>
      
        </div>
      );
    }
  }
export default EditSTAFF;
