import React from "react";
import {Form, Input, Button, Alert} from 'reactstrap';
import HeaderPage from "./header";
import FooterPage from "./footer";
import {Link} from "react-router-dom";
import SidebarPage from "./sidebar";
import firebase from '../config';
import ReactQuill from "react-quill";
import SimpleReactValidator from "simple-react-validator";
import MultiSelectReact from 'multi-select-react';

class AddNotifications extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            pageTitle: '',
            pageName: '',
            date: new Date().toLocaleString(),
            content: '',
            theme: 'snow',
            notification_type: '',
            merchantList: [],
            merchants: '',
            multiSelect: [],
            categoryList:[],
            category:'',
        }
       
        this.validator = new SimpleReactValidator({
            className: "text-danger",
            validators: {

                whitespace: {
                    message: "The :attribute not allowed first whitespace   characters.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /[^\s\\]/) && params.indexOf(val) === -1);
                    }
                },
                specialChar: {
                    message: "The :attribute not allowed special   characters.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /^[ A-Za-z0-9_@./#&+-]*$/i) && params.indexOf(val) === -1);
                    }
                },
                specialCharText: {
                    message: "The :attribute may only contain letters, dot and spaces.",
                    rule: function (val, params, validator) {
                        // return validator.helpers.testRegex(val,/^[a-zA-Z0-9]{6,30}$/i) &&
                        // params.indexOf(val) === -1
                        return (validator.helpers.testRegex(val, /^[ A-Za-z_@./#&+-]*$/i) && params.indexOf(val) === -1);
                    }
                }
            }
        });

    }
    modules = {
        toolbar: [
            [
                {
                    'header': [
                        1,
                        2,
                        3,
                        4,
                        5,
                        6,
                        false
                    ]
                }
            ],
            [
                'bold', 'italic', 'underline', 'strike', 'blockquote'
            ],
            [
                {
                    'list': 'ordered'
                }, {
                    'list': 'bullet'
                }, {
                    'indent': '-1'
                }, {
                    'indent': '+1'
                }
            ],
            [
                'link', 'image'
            ],
            ['clean'],
            [
                {
                    'color': []
                }, {
                    'background': []
                }
            ]
        ]
    };

    formats = [
        'header',
        'bold',
        'italic',
        'underline',
        'strike',
        'blockquote',
        'list',
        'bullet',
        'indent',
        'link',
        'image',
        'color',
        'background'
    ];
    onChange = (event) => {

        this.setState({
            [event.target.name]: event.target.value
        });
    };

    handleChange=(value) => {
        this.setState({content: value})
    };

    handleSubmit = (event) => {
        event.preventDefault();
        if (this.validator.allValid()) {
            if (this.state.pageTitle && this.state.pageTitle.trim().length !== 0 && this.state.content.trim().length !== 0) {

                let dbCon = firebase
                    .database()
                    .ref('/Notifications');
                dbCon.push({
                    pageTitle: this.state.pageTitle,
                     notification_type: this.state.notification_type,
                      pageName: this.state.pageName, 
                      category: this.state.category,
                    content: this.state.content,
                    merchantList:this.state.multiSelect,
                     created_on: this.state.date});

                this.setState({pageTitle: '', content: '', pageName: ''});
                this
                    .props
                    .history
                    .push("/Notifications/Created");
            }

        } else {
            this
                .validator
                .showMessages();
            this.forceUpdate();
        }
    };
    
    optionClicked(optionsList) {
        this.setState({multiSelect: optionsList});
        console.log(this.state.multiSelect);
    }
    selectedBadgeClicked(optionsList) {
       console.log(optionsList);
        var sortedKeys = optionsList.filter(res=>{ 
         console.log(res);
            return res.value; });
            console.log(sortedKeys);
      
        this.setState({ multiSelect: sortedKeys}, () => {
            console.log(this.state.multiSelect, 'multiSelect');
            });
        
    }
    componentDidMount() {
        this.setState({loading: true});
        var ref = firebase
            .database()
            .ref("admin_users/")
            .orderByChild("Role")
            .equalTo("Merchant");

        ref.on('value', snapshot => {
            const data = [];
            snapshot.forEach(childSnapShot => {

                const GSTData = {

                    label: childSnapShot
                        .val()
                        .email,
                    id: childSnapShot
                        .key
                        .toString()
                };

                data.push(GSTData);
            });

            this.setState({merchantList: data});
            console.log(this.state.merchantList);
        });
        this.categoryLists();
    }
    categoryLists() {
        this.setState({ loading: true });
        var ref = firebase.database().ref("category/");
    
    
        ref.on('value', snapshot => {
            const data = [];
          
    
            snapshot.forEach(childSnapShot => {
               
              const Categories = {
                categoryId: childSnapShot.key.toString(),
                category: childSnapShot.val().category,
                created_on: childSnapShot.val().created_on,
              };
      
              data.push(Categories);
            });
      
            this.setState({ categoryList:data,loading: false });
            console.log(this.state.categoryList);
          }); }
    render() {

        const divStyle = {
            'height': '300px'
        };
        const selectedOptionsStyles = {
            color: "#3c763d",
            backgroundColor: "#dff0d8"
        };
        const optionsListStyles = {
            backgroundColor: "#dff0d8",
            color: "#3c763d"
        };

        return (
            <div>
                <SidebarPage/>

                <div className="all-content-wrapper">
                    <HeaderPage/>

                    <div className="breadcome-area">
                        <div className="container-fluid">
                            <div className="row">
                                <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                    <div className="breadcome-list">
                                        <div className="row">
                                            <div className="col-lg-6 col-md-6 col-sm-6 col-xs-6">
                                                <div className="breadcomb-wp">

                                                    <div className="breadcomb-ctn">
                                                        <h2>Notifications</h2>
                                                    </div>
                                                </div>
                                            </div>

                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                    <div className="single-product-tab-area mg-b-30">

                        <div className="single-pro-review-area">
                            <div className="container-fluid">
                                <div className="row">
                                    <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                        <div className="review-tab-pro-inner">

                                            <h4>Add Notifications</h4>

                                            

                                            <div className="product-tab-list tab-pane fade active in">
                                                <Form onSubmit={this.handleSubmit} id="Edit-GST">

                                                    <div className="row m-b-20">
                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                        <label>Page Title<span style={{color: 'red' }}>*</span></label> 
                                                            <input
                                                                type="text"
                                                                className="form-control"
                                                                placeholder="Page Title*"
                                                                id="add_GST"
                                                                name="pageTitle"
                                                                onChange={this.onChange}
                                                                value={this.state.pageTitle}/> {this
                                                                .validator
                                                                .message("Page Title", this.state.pageTitle, "required|specialChar|min:3|max:70")}
                                                            <div className="text-danger">
                                                                {" "}
                                                                {this.state.employer_sevice_message}
                                                            </div>
                                                        </div>

                                                    </div>
                                                    <div className="row m-b-20">
                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                        <label>Page Name<span style={{color: 'red' }}>*</span></label> 
                                                            <input
                                                                type="text"
                                                                className="form-control"
                                                                placeholder="Page Name*"
                                                                id="add_GST"
                                                                name="pageName"
                                                                onChange={this.onChange}
                                                                value={this.state.pageName}/> {this
                                                                .validator
                                                                .message("Page Name", this.state.pageName, "required|specialChar|min:3|max:70")}
                                                            <div className="text-danger">
                                                                {" "}
                                                                {this.state.employer_sevice_message}
                                                            </div>
                                                        </div>

                                                    </div>
                                                    <div className="row m-b-20">
                                                    <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                    <label>Category Name<span style={{color: 'red' }}>*</span></label>   
                            <select
                                                            name="select"
                                                            className="form-control pro-edt-select form-control-primary"
                                                            name="category"
                                                            onChange={this.onChange}>
                                                             <option>Select Category</option>
                                                             {this.state.categoryList && this.state.categoryList.map((data,index) =>{
                                       
                                       
                                        return(
                                            <option value={data.category} key={index}>{data.category}</option>
                                        )
                                       

                                    } )}
                                     
                                     
                                                           
                                                        </select>
                                                        {this.validator.message(
                                            "Select category",
                                            this.state.category,
                                            "required"
                                          )}
                           
                          
                            </div>

                                                    </div>
                                                    <div className="row m-b-20">
                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <div className="form-group">
                                                                <label className="col-lg-3 control-label">To<span style={{color: 'red' }}>*</span></label>
                                                                <div className="col-sm-8">
                                                                    <div className="row">
                                                                        <div className="col-sm-3">
                                                                            <label className="radio-inline no-padd">
                                                                           
                                                                                <input
                                                                                    type="radio"
                                                                                    name="notification_type"
                                                                                    value="All"
                                                                                    onChange={this.onChange}
                                                                                    checked={this.state.notification_type === 'All'}/>
                                                                                All
                                                                            </label>
                                                                        </div>

                                                                        <div className="col-sm-3">
                                                                            <label className="radio-inline no-padd">
                                                                                <input
                                                                                    type="radio"
                                                                                    name="notification_type"
                                                                                    value="Users"
                                                                                    onChange={this.onChange}
                                                                                    checked={this.state.notification_type === 'Users'}/>
                                                                                Users
                                                                            </label>
                                                                        </div>

                                                                        <div className="col-sm-3">
                                                                            <label className="radio-inline no-padd">
                                                                                <input
                                                                                    type="radio"
                                                                                    name="notification_type"
                                                                                    value="Merchants"
                                                                                    onChange={this.onChange}
                                                                                    checked={this.state.notification_type === 'Merchants'}/>
                                                                                Merchants
                                                                            </label>
                                                                        </div>

                                                                    </div>
                                                                    {this
                                                                        .validator
                                                                        .message("Send To", this.state.notification_type, "required")}
                                                                </div>
                                                            </div>

                                                        </div>

                                                    </div>
                                                    {this.state.notification_type === 'Merchants'?
                                                    <div className="row m-b-20">
                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                            <div className="form-group">
                                                            
                                                              
                                                               
                                                                    <MultiSelectReact
                                                                        options={this.state.merchantList}
                                                                        optionClicked={this
                                                                        .optionClicked
                                                                        .bind(this)}
                                                                        selectedBadgeClicked={this
                                                                        .selectedBadgeClicked
                                                                        .bind(this)}
                                                                        selectedOptionsStyles={selectedOptionsStyles}
                                                                        optionsListStyles={optionsListStyles}
                                                                        isTextWrap={false}/>
                                                                </div>

                                                            </div>
                                                        </div>:''}

                                                   
                                                    <div className="row m-b-20">
                                                        <div className="col-lg-6 col-md-6 col-sm-6 col-xs-12">
                                                        <label>Description<span style={{color: 'red' }}>*</span></label> 
                                                            <ReactQuill
                                                                theme={this.state.theme}
                                                                value={this.state.content}
                                                                modules={this.modules}
                                                                formats={this.formats}
                                                                placeholder='Enter Text'
                                                                onChange={this.handleChange}
                                                                className="add-new-post__editor mb-1"
                                                                style={divStyle}/>

                                                        </div>

                                                    </div>

                                                    <div className="row m-b-20"></div>
                                                    <div className="row m-b-20"></div>
                                                    <div className="row m-b-20">
                                                        {this
                                                            .validator
                                                            .message("Page Content", this.state.content, "required|min:50")}
                                                        <div className="text-danger">
                                                            {" "}
                                                            {this.state.employer_sevice_message}
                                                        </div>

                                                    </div>
                                                    <div className="row m-b-20">
                                                        <div className="col-lg-6 col-md-6 col-sm-12 col-xs-12">
                                                            <div className="text-left custom-pro-edt-ds">

                                                                <button type="submit" className="btn btn-ctl-bt waves-effect waves-light">Add</button>
                                                                <Link to="/Notifications">
                                                                    <button type="button" className="btn btn-ctl-bt waves-effect waves-light">Cancel
                                                                    </button>
                                                                </Link>

                                                            </div>
                                                        </div>
                                                    </div>
                                                </Form>
                                            </div>

                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
                <FooterPage/>

            </div>
        );
    }
}

export default AddNotifications;
